package com.samuel.oremoschangana.apresentacaoOracao

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.samuel.oremoschangana.apresentacaoOracao.CancaoState
import com.samuel.oremoschangana.dataOracao.Cancao
import com.samuel.oremoschangana.dataOracao.CancaoDao
import kotlinx.coroutines.ExperimentalCoroutinesApi
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.SharingStarted
import kotlinx.coroutines.flow.combine
import kotlinx.coroutines.flow.first
import kotlinx.coroutines.flow.flatMapLatest
import kotlinx.coroutines.flow.stateIn
import kotlinx.coroutines.launch


class CancoesViewModel( private val cdao: CancaoDao): ViewModel() {

    private val isSortedByNumber = MutableStateFlow(true)

    @OptIn(ExperimentalCoroutinesApi::class)
    private var cancoes =
        isSortedByNumber.flatMapLatest{ sort ->
            if (sort){
                cdao.getCancaoOrderByNumero()
            }else{
                cdao.getCancaoOrderByNumero()
            }
        }.stateIn(viewModelScope, SharingStarted.WhileSubscribed(), emptyList())

    val _state = MutableStateFlow(CancaoState())

    val cstate =
        combine(_state, isSortedByNumber, cancoes){state, isSortedByNumber, cancoes ->
            state.copy(
                cancoes = cancoes
            )
        }.stateIn(viewModelScope, SharingStarted.WhileSubscribed(5000), CancaoState())

    init {
        viewModelScope.launch {
            val listaCancoes = cdao.getCancaoOrderByNumero().first()
            if (listaCancoes.isEmpty()) {
                inserirDadosAutomaticamente()
            }
        }
    }

    private fun inserirDadosAutomaticamente() {
        val cancoesParaInserir = listOf(
                Cancao("1","Entrada","A hi fambeni, hi ya gandzela(236)",""," Coro: A hi fambeni, a hi fambeni,\n hi ya gandzela Hosi. \n\n  1 - Hosi wa fuma, u yambale wukulukumba.\n 2 - Matilo ma tsakile, misava yi tlangela Hosi.\n 3 - Ku kwetsima ka Wena Hosi, hi la ku nga heliki.\n 4 - Hosi hi munene, a fuma hi ku rhula.\n 5 - Tanani, yi yimbelela, hi huwelela hi ku tsaka.\n 6 - Tanani hi khinsamela Hosi Muvumbi wa hina.\n 7 - Nwina valulami dzunisani Vito la Yena.", false),

                Cancao("2","Entrada","A hi fambeni hinkwerhu(1)","","  Coro: A hi fambeni hinkwerhu le ka Hosi Bava (3 v.)\n Ku tsakeni lo'ku nga heliki.\n\n\n  1 - Hi tshama na Yesu, na Yena Bava (3 v.)\n Ku tsakeni lo'ku nga heliki.\n\n 2 - A ku na livengo le ka Yena Bava (3 v.)\n Ku ni ku tsaka lo'ku nga heliki.\n\n 3 - A ku na wukwele le ka Yena Bava (3 v.)\n Ku ni ku tsaka lo'ku nga heliki.", false),

                Cancao("3","Entrada","Ha lava ku ku landza (2)","","  1 - ha lava ku ku landza, ina, ha kunen\n\n  Coro: Xikwembu, hi lungisele ndlela leyi.\n\n  2 - Ka hinkwaswo swa swinene, ina hi ta swi landza (2x)\n\n  3 - Ndza swi lava ku ku rhandza, ina ha kunene (2x)\n\n  4 - Ndza swi lava ku yingisa, ina ha kunene (2x).", false),

                Cancao("4","Entrada","Hambi ndlela yi khumile (3)",""," Coro:Hambi ndlela yi khumile\n N'ta mu landzela (3)\n Hambi ndlela yi khumile\n N'ta mu landzela\n Minkondzweni ya Yesu Hosi yanga.\n\n  1 - Wutomi la mina\n nyamuntlha\n a li fani ni la khale,\n Ndzwalo wa swidjoho ni swivi\n wu swusiwile hinkwawo\n\n  2 - A ndzi li karhi ndzi tshama\n Munyameni lo'wukulu\n a ndzi nga mu tivi Muhanyisi\n a ndzi nga voni ku tsaka.\n\n  3 - Kambe Moya wa Hosi\n a wu ba mbilu ya mina\n na wu ndzi kombisa swidjoho\n ni ndlela ya ku ponisiwa.\n\n  4 - A ndzi langutela ka Yesu\n na ndzi tivula hinkwaswo,\n a ndzi nghena hi ku pfumela\n Tiveni la Ngati ya Yesu.", false),

                Cancao("5","Entrada","Hi tele ku ta gandzela we Hosi","","  1 - Hi tele ku ta gandzela we Hosi (2x)\n Hi pfulele nyangwa yaku (2X)\n\n  Coro: Hosi, hi nyike ntamu,\n wa ku ku landza, tsama na hina mbilwini (bis)\n\n  2 - Hi teke ku ta lava we Hosi (2x).\n Hi tele ku ta twalisa we Hosi (2X)\n\n  3 - Hi tele ku ta kombela we Hosi (2x).\n Hi tele ku ta khongela we Hosi\n\n  4 - Hi tele ku ta tlhangela we Hosi(2x).\n Hi tele ku ta dzunisa we Hosi", false),

                Cancao("6","Entrada","Hina ha nghena ndlwini ya Xikwembu (5)","","  Coro: Hina hi nghena\n ndlwini ya Xikwembu\n Hi ta khongela, hi ta khongela\n Yingisa, yingisa mikombelo leyi,\n Hosi Yesu.\n\n  1- Hi kola ka ku kholwa\n Hina hi ta laha\n Hi ta ku kombela\n Ntamu wa lirhandzu\n\n  2- Hosi yamukela\n Ntirho wa hina\n Hi ku nyikaka\n Hi mbilu hinkwayo\n\n  3 - Siku la nyamuntlha\n I siku lo tsaka\n A hi twaliseni\n Ka Hosi Nkulukumba\n\n  4 -  Siku la nyamuntlha\n Hi le ku tsakeni\n Hi ku ti nyikela\n Ka vamakwerhu lava.\n\n  5 - Tanani vamakwerhu\n Hi ta khongela\n Hi bonga ka Bava\n Nyiko yo vitaniwa.", false),

                Cancao("7","Entrada","Hosi ha nghena ndlwini yaku (4)",""," Coro: sol.:\n Todos:\n Hosi ha nghena ndlwini yaku\n Hina ha nghena, a hi pfuleni timbilu\n Hina ha nghena. (2x)\n (4a Voz)\n\n  1 - Hi ta twalisa hi ta dzunisa.\n\n  2 - Hi ta gandzela hi ta khongela.\n\n  3 - Hi ta khensa hi ta Tsaka.\n\n  4 - Hi ta yimba hi ta tlangela.\n\n  5 - Hi ta kombela hi ta nyikela.", false),

                Cancao("8","Entrada","Hosi ndzi pfumele",""," Coro: Hosi, ndzi pfumele, ndzi pfuleli ndzi nghena.\n Wena, Hosi ndza tisola ndzi ta, khuluma na wena ( 2x)\n\n  1 - Ndzi gongondza,impela a nyangweni\n A wu ndzi twi, a wu ndzi twi\n Ndzi pfulele ke ndzi hghena\n Nkulukumba wa Tilo hinkwalo.\n\n  2 - Ndzi sungula a mhamba\n Ya siku la nyamuntla\n Ndzi yingise, ndzi nyike ntamu\n Wa ku kutisola wena\n Hosi ya ntamu hinkwawo.\n\n  3 - Wena Hosi ya tilo ni misava\n Nyeleti ya livaningo\n Ndzi khanyisele ke ndzi nghena\n Ndzi ku khisamela, ndza ku dzunisa.\n\n  4 - Ndzi hlayise ni le yetheleni,\n U Mongola mimoya yo biha\n U txhela moya wa ku basa\n A ku pfukeni ndzi ku dzunisa.\n\n  5 - Ndzi kombelela a vahlawuliwa\n U va nyika a matimba\n Ya ku txhumayela a rito\n A rito la Ivangeli.", false),

                Cancao("9","Entrada","Hosi nkulukumba weru (15) (Ronga)","","  1 - Hosi, Nkulukumba werhu,\n Hi vone hi thlanganiki.\n Hi teli ku ta gandzela\n Wene, Hosi, ni Tatana.\n\n  Coro:\n Ha ku kombela, we Hosi,\n Yamukela vana vaku,\n U va hanana tindjombo\n Ta wutomi le'byinene.\n\n  2 - A xikhongoto xa Misa\n Le'xi nga gandzelo dzeru\n Ha ku bekela namunthla\n Xi yamukeli Tatana.\n\n  3 - Katekisa tiku dzeru\n Tamela mimoya yeru\n Hosi, hi hanani ntamu\n Wa ku tsama hi khongela.", false),

                Cancao("10","Entrada","Huwelelani nwina (6)","","Coro: Huwelelani nwina\n va tiko la ka Gaza\n Huwelelani hi ku tsaka\n mi dzunisa a Hosi ya nwina\n Leyi yi nga mi pfuna a ku heta\n a matxhume manbirhi ni ntlhanu\n wa malembe, huwelelani!\n\n  1 - Murhangeli wa hina Yohane Mubabatise\n Hi kombise ndlela\n\n  2 - Tiko la mibangu hi la maxangu,\n Huwelela Hosi\n\n  3 - Hambi loko tiko leli li dla mbitsi ni nhloti,\n Huwelela Hosi", false),

                Cancao("11","Entrada","Le thepeleni la Hosi","","Coro: Le thepeleni la Hosi,\n hi fanele ku nghena hi ku tsaka\n le thepeleni la Hosi, hi fanele ku yimba aleluia\n\n  1 - S: vuya we Hosi ni matimba\n hi ta kota ku yimba.\n T: hi fanele ku yimba aleluia (2v)\n S: makholwa vuyani hi patsana hi yimba\n T: hi fanele ku yimba aleluia\n\n  2 -Nfumu wa Hosi hakunene wu tatile misava…\n makholwa vuyani hi patsana hi yimba\n hi fanele ku yimba alelui\n\n  3 - Misava yimpswha yi xikele,\n vuyani hi gandzela...\n makholwa vuyani hi patsana hi yimba\n hi fanele ku yimba alelui\n\n  4 - Pfula we Hosi nyangwa yaku\n hi nghena hi gandzela...\n makholwa vuyani hi patsana hi yimba\n\n  5 - Tlakusa mahlo, lava Hosi, hi ta kota ku yimba...\n na hi gandzela hi fanele ku yimba…", false),

                Cancao("12","Entrada","Namunhla hi siku la Hosi","","1 - Namunhla hi siku la Hosi, buyani,\n buyani hi khongela.\n\n  Coro: Buyani, buyani, buyani, buyani hi khongela (2x).\n\n  2 - Namunhtlha hi siku la ku khongela..\n\n  3 - Namuntlha hi siku la nhoxa,.\n\n  4 - Namuntlha hi siku la ku tlhangela..\n\n  5 - Namuntlha hi siku la ku yimba...", false),

                Cancao("13","Entrada","Ndri rhandra ku nhingena (16) (Ronga)","","Coro: Ndri rhandra ku\n nhingena ndlwini\n ya Nkulukumba.\n Ndri nhingena ndlwini ya ku xwenga\n Ndri rhandra ku khongota\n\n  1 - Nwananga nhingena ndlwini ya ku xwenga\n U drimuka a ku bekisa nawu wanga\n\n  2 - Nwananga bekisa tinfanelo taku\n U nga ti tshika u wela ndringweni wa nala\n\n  3 - Loko va ku ruka nambi va nga ku hleka\n U nga heli mbilu, khongota Hosi yaku\n\n  4 - Loko u yentxa hinkwaswo swileleto swa Hosi\n U ta yaveliwa swa nfumu wa matilo.", false),

                Cancao("14","Entrada","Nghena tempeleni (7)","","1 - Nghena tempeleni nwananga, aleluia\n U luva Hosi ya wena, aleluia,\n nyamunthla i siku la ku tsaka\n nghena tempeleni nwananga, aleluia\n\n  2 - Tshika titshomba ta misava, aleluia\n pfumela Hosi ya wena, aleluia;\n nyamunthla i siku la ku tsaka\n tshika titshomba ta misava, aleluia\n\n  3 - Pfula a mbilu ya wena, aleluia\n yamukela muvumbi waku, aleluia;\n nyamunthla i siku la ku tsaka\n pfula a mbilu ya wena, aleluia\n\n  4 - Thlakusa mahlo ya wena, aleluia\n txhuvuka Hosi ya wena, aleluia;\n nyamunthla i siku la ku tsaka;\n thlakusa mahlo ya wena.", false),

                Cancao("15","Entrada","Nkulungwana wa tsaka","","1 - Nkulungwana wa ku tsaka\n Massungulo ya lirhandzu\n Ka Wena wo Kwetsima\n\n  Coro:\n We Hosi, hi tlanganile(2X)\n Hi ta dzunisa vito laku\n Vito laku lo Kwetsim\n\n  2 - Tanani vamakwerhu\n Vakulu ni vatsongwani\n Hi ta gandzela Xikwembu. (bis\n\n  3 - Ka Hosi hi tlanganile\n Hi ta dzunisa Xikwembu\n ni wunharhu lo Kwetsima\n\n  4 - Banani nkulungwani\n Hi ta dzunisa siku leli\n Siku leli lo kwetsima.", false),

                Cancao("16","Entrada","Pfumela wa vitaniwa (94)","","Coro: Pfumela wa vitaniwa (3 v.)\n Le muntini wa Xikwembu\n\n  1 - Tana wena u nga ni ndlala (2 v.)\n U ta dla muzimba wa Yesu\n Le muntini wa Xikwembu\n\n  2 - Tana wena u nga ni torha (2 v.)\n U ta phuza Ngati ya Yesu\n Le muntini wa Xikwembu\n\n  3 - Tana wena u karhaleke (2 v.)\n Tana wena u nga xisiwana\n Le muntini wa Xikwembu\n\n  4 - Tshunelani nwina hinkwenu (2 v.)\n Yena Yesu a mi vitana\n Le muntini wa Xikwembu\n\n  5 - Yingisani, nwina makholwa, (2 v.)\n A marito ya Hosi Yesu\n Le muntini wa Xikwembu\n\n  6 - Rito lakwe la hanyisa (2 v.)\n Rito le'li nyika wutomi\n Le muntini wa Xikwembu.", false),

                Cancao("17","Entrada","Sekeleka hi famba (8)","","SOL: Sekeleka Hi Famba (4X)\n TOD: Ka Xikwembu sekeleka hi famba ka Xikwembu.(4X)\n Aleluya Ka Xikwembu Sekeleka hi famba.\n\n  1 - Hi ya lava lirhandzu.(4X)\n\n  2 - Hi ya lava a ntamu.(4X)\n\n  3 - Hi ya lava ku tsaka. (4X)\n\n  4 - Vapfumeli a hi yeni.(4X)\n\n  5 - Va Bixopo a hi yeni. (4X)\n\n  6 - Vafundhisi a hi yeni.(4X)\n\n  7 - Makhosazana a hi yeni.(4X)", false),

                Cancao("18","Entrada","Siku la nyamuntlha (10)","","1 - Siku la nyamunthla, aleluia, Ámen,\n I siku lo wisa, Aleluia, Ámen.\n Hi tshike mintirho, hi lava Xikwembu,\n Hosi ya matilo, Aleluia, Ámen.\n\n  2 - Siku la nyamuntlha, Aleluia, Ámen,\n I siku lo bonga, Aleluia, Ámen.\n Tanani, vamakwerhu, a hi bongeni Yesu\n Hi nomu ni mbilu, Aleluia, Ámen.\n\n  3 - Siku la nyamunthla, Aleluia, Ámen,\n I siku lo tsaka, Aleluia, Ámen.\n Yesu a pfukile, swoswi wa hanya\n Kambe a fuma, Aleluia, Ámen.\n\n  4 - Siku la nyamunthla, Aleluia, Ámen,\n I siku lo hanya, Aleluia, Ámen.\n Hi ni Muhuluxi, wa nyika wutomi\n Le'li nga heliki, Aleluia, Ámen.", false),

                Cancao("19","Entrada","Siku la nyamutlha li katekile (9)","","1 - Siku la nyamuntlha li katekile;\n Hi te ta tlhangana a ku tsakeni.\n Miringo yi tele, hi yi hlulile\n ntamu wa ku fika ni wu kumile.\n\n  Coro:A hi tlhangeleni a siku leli\n La ntlangano weru,\n mahlweni ka Hosi.\n\n  2 - Siku la nyamuntlha li katekile;\n hi tlhanganisiwe hi ntamu wa Yesu\n a hi mu luveni hi mbilu ni moya\n Khanimambo, Yesu Muhuluxi weru.\n\n  3 - Siku la nyamuntlha li katekile;\n li hi nyika ntamu a wutonwini\n a hi tlangeleni ka Hosi Yesu\n Tatana Lwe'nene muvekisi weru.", false),

                Cancao("20","Entrada","Tana wa ku vita Yesu (11)","","  Coro: Tana, wa ku vita Yesu\n Lw'a nga ta ku hanyisa;\n A ni lirhandzu na wena,\n A ta ku nyika hinkwaswo.\n\n  1 - Leswi u swi pfumalaka\n Swa nandziha ka va na Ye.\n Ku hlupheka ni ku rila\n U naku la misaveni.\n\n  2 - Tana la ka Yena Yesu;\n A ta ku nyika tindjombo,\n Wutomi la le tilweni\n L'a tshamiseke kona.\n\n  3 - Yingisa marito yakwe\n la ma nge: hi mina Yesu,\n Murisi waku Lwe'nene,\n Hi mina ndzi nga ku fela.\n\n  4 - Ndzi ku hlampswe hi ngati yanga\n a henhla ka xihambano.\n Ndza fele wena, mudjohi;\n Tshika a ku ndzi djohela.\n\n  5 - Khumbuka masiku yaku,\n Ma kusuhi ni ku hela;\n Tiva Hosi Nkulukumba;\n A ku vitanaka swoswi.", false),

                Cancao("21","Entrada","Vamakwerhu tanani (13)","","Sol: Vamakwerhu, tanani\n Tod: tanani hi ta gandzela Hosi.\n Sol: Vamakwerhu tanani ho.\n Tod:  tanani hi ta gandzela na hi tlhanganile.\n\n  1 - Va João Baptista (2X) \n\n  2 - Va Xidenguele (2X)\n\n  3 - Va Chongoene (2X)\n\n  4 - Va Chikumbane (2X).", false),

                Cancao("22","Entrada","Vuyani (17)","","Sol:  Vuyani, vuyani, vuyani,\n Tod: Vuyani hi ta gandzela Hosi\n Sol: Hi ta\n Tod: Hi ta rivaleliwa swidjoho swerhu (2x).\n\n  1 – Vafundisi\n\n  2 - Vairmã\n\n  3 – Vatatana\n\n  4 - Vamamana\n\n  5 – Vanhwanyana\n\n  6 - Nwi madjaha\n\n  7 – Vakokwana\n\n  8 - Vatsongwana\n\n  9 - Nwina hinkwerhu", false),

                Cancao("23","Entrada","Yesu a yimi nyangweni (18)","","1. Yesu a yimi nyangweni,\n Nyangweni ya mbilu yaku;\n A yimile khale kone,\n A li: pfula nwananga.\n\n  2 - Ku thlasa nkama wa swoswi\n A wu mu yamukelanga\n U hinghenise Sathana.\n Lw'a nga nala waku.\n\n  Coro: Mu pfulele nyangwa yaku\n U nga nonohe mbilwin;\n Hi Yesu lw'a gongondzaka,\n Pfula, a ta hinghena. (2)\n\n  3 - A fa u tshavela byala\n Le'byi dlayisaka vanhu;\n Swi hila le'swa ku biha\n Swi ku vangeli lifo.\n\n  4 - A ku si tiva lisuna\n Lo'li twalaka ha Yesu:\n Loko u ta va na Yena,\n U ta nkhensa hinkwaswo.\n\n  5 - A ta hlongolisa nala\n Hi ku hlaya rito ndzinwe;\n A ta ku hanana ntamu\n Wa ku hlula mindzingo.\n\n  6 - Yamukela Hosi Yesu!\n Mu pfuleli a hinghena;\n Yingela wa ha gongondza;\n Hlaya: Hosi, hinghena!", false),

                Cancao("24","Entrada","Yimbelela Hosi (14)","","  Coro:\n Yimbelela Hosi (3x)\n Ya matilo aleluia Ámen.\n\n  1 - Nambi loko swidjoho\n swa misava, swi ku tikela \n mbilwini makwerhu pfula \n nomu wa wena, yimbelela \n Hosi ya wena.\n\n  2 - Nambi loko makwehnu a\n ku djohela, a lwa ni u \n pfumeli la wena, makwerhu\n pfula nomu wa wena, \n yimbelela Hosi ya wena.\n\n  3 - Tivoneli marengu ya \n mubihi lawa ma lwaka ni\n wu pfumeli la wena, \n makwerhu pfuna nomu wa\n wena, yimbelela Hosi \n ya wena.", false),

                Cancao("25","ActoPenitencial","Hosi hi twele wusiwana","","Sol.:\n Hosi, hi twele wusiwana\n Todos: Hosi, hi twele wusiwana(2x)\n Hosi, Hosi, Hosi.\n\n  Sol.:\n Kriste, hi twele wusiwana\n Todos: Kriste, hi twele wusiwana(2x)\n Kriste, Kriste, Kriste.\n\n  Sol.:\n Hosi hi twele wusiwana\n Todos: Hosi hi twele wusiwana(2x)\n Hosi, Hosi, Hosi.", false),

                Cancao("26","ActoPenitencial","Hosi hi yentxele wurombe (Ronga)","","Sol.: Hosi (5x) we Tatana\n Tod.: Hi yentxele wurombe we Tatana.\n\n  1 - Ku sungula a hi dlaya\n Ka vubidri hi xandrana\n Swi ta hela drini Hosi Wurombe we Tatana\n\n  2 - A Kereke dra hi laya\n Kambe a kudlaya ahitrike\n Hosi hi yentxele wurombe. (2x)", false),

                Cancao("27","ActoPenitencial","Hosi hina ha tiwisa hansi","","1 - Hosi hina ha tiwisa hansi\n Hi djohile Hosi, hi djohile hi rivalele\n Nala a hi kanganyisile hi lahla ndlela ya wena\n Hosi hina ha tiwisa hansi.\n\n  2 - Kristo hina ha tiwisa hansi,\n Hi djohile Kristo, hi djohile hi rivalele,\n Nala a hi kanganyisile hi lahla ndlela ya wena\n Kristo hina ha tiwisa hansi.\n\n  3 - Hosi hina ha tiwisa hansi\n Hi djohile Hosi, hi djohile hi rivalele\n Nala a hi kanganyisile hi lahla ndlela ya wena\n Hosi hina ha tiwisa hansi.", false),

                Cancao("28", "ActoPenitencial", "Lesw hosi hi ku djohelike", "", "1 - Leswi Hosi hi ku djohelike\n Hi tsika ndlela ya wene\n Hi kokiwa hi swa misava.\n Hi rivalele Hosi. (2x)\n \n Hi kombela revalelo, Hosi (3x)\n Leswi hi ku djohelike.\n \n   2 - Leswi Cristo hi ku djohelike\n Hi ksika ndlelaya wene\n Hi kokiwa hi swa misava\n Hi rivalele, Cristo.\n \n i kombela rivalelo, Cristo (3x) \n Leswi hi ku djoyelike. \n \n  3 - Leswi Hosi hi Ku djohelike \n Hi tshika ndlela ya wena \n Hi kokiwa hi swa misava \n Hi rivalele Hosi. (2x) \n Hi kombela rivalelo, Hosi (3x) \n Leswi hi ku djohelike", false),

                Cancao("29","ActoPenitencial","Mini Hosi (Ronga)","","1 - Mine Hosi\n  mahlweni kwaku\n  ndzi lwe mutrongo\n a swi ndzi faneli\n  u ta ku mine.\n\n Coro: Ni drivaleli Hosi swihono swanga\n  Hi wumbilu braku ni drivaleli.\n\n 2 - Kriste Hosi\n mahlweni kwaku\n ndzi lwe mutrongo\n  a swi ndzi faneli\n u ta ku mine.\n\n 3 - Mine Hosi\n mahlweni kwaku\n ndzi lwe mutrongo\n  a swi ndzi faneli\n u ta ku mine.", false),

                Cancao("30","ActoPenitencial","Ndza ha tsunela","","1. Ndza ha tsunela le ka Hosi\n Ndzi rila mahlweni ka yena (2x)\n  Oh Hosi - Hosi ndzi mudjohi\n Oh Hosi - ndzi twele wusiwana\n\n 2. Ndza ha khizama la ka Kriste\n  Ndzi rila mahlweni ka yena (2x)\n Kriste - Kriste ndzi mudjohi\n Kriste - ndzi twele wusiwana\n\n 3. Ndza ha tiwisa la ka Hosi\n Ndzi rila mahlweni ka yena (2x)\n  Oh Hosi - Hosi ndzi mudjohi\n Oh Hosi - ndzi twele wusiwana", false),

                Cancao("31","ActoPenitencial","Ndzi ta rivalela kangani","","1 - Ndzi ta rivalela kangani\n  loko makwerhu a ndzi djohela?\n Ndzi ta rivalela kangani\n kumbe ka ntlhanu na kambirhi?\n\n Tek: Tatana, ndzi pfune, ndza kana-kana,\n  A ndzi swi koti a ku rivalela.\n  Hosi, ndzi djondzise ku rivalela\n  Loko makweru a ndzi djohela.\n\n 2 - Leswi taleke la misaveni\n  I ku rihisa tihlo hi tihlo\n  Leswi taleke la misaveni\n  Hi ku tlheliselana ntsena.\n\n 3 - A hi tsundzukeni rito leli\n  U nga tlhelise hi le swo biha\n Loko makwerhu a ku djohela\n Mu tlhelisele hi timpswalo.", false),

                Cancao("32","Gloria","A ku twalisiwe Hosi (25)","","Coro: A ku twalisiwe Hosi, Nkulukumba matilweni\n  a ku ve ni rhula misaveni (bis).\n\n 1 - Nkulukumba wa matilo,\n Tatana wa ntamu hinkwawo\n  Hi kola ka ku phatima kwaku lo'kukulu\n dzunisiwa, tlangeliwa, gandzeliwa,\n twalisiwa, u va U khensiwa.\n\n 2 - Yesu Kristo U nga Nwana wa Xikwembu\n Yesu Kristo Xihambana xa Xikwembu\n  U susaka a swihono swa misava\n Oh Hosi hi twele wusiwana.\n\n 3 - Yesu Kristo u nga vokweni la Tatana\n Yesu Kristo hi twele wusiwana\n  Wene Hosi, hi Wene ntsena U phatimaka\n U fumaka ni Moya wa ku Xwenga.", false),

                Cancao("33","Gloria","A twalisiwe nkulukumba (Ronga)","","Coro:\n A twalisiwe, Nkulukumba\n  A henhla, henhla matilweni.\n\n 1 - Ni ku rula akuve kone\n A misaveni\n Ku vhanu va ku randriwa\n  Ha yene.\n  Nkulukumba Hosi\n Ya matilo\n  Nkulukumba Tatana\n Wa ntamu hinkwawu.\n\n 2 - Ha ku drumisa\n Ha ku khensa\n Ha ku khinsamela\n Ha ku twalisa.\n Ha ku tlangela\n Hikola ka lidrumu\n  Lidrumu draku\n Le dri kulu.\n\n 3 - Hosi Yesu Kriste\n  U nga Nwana wa wuswaku\n  Hosi Nkulukumba\n Xihambana xa Nkulukumba.\n Nwana wa Nkulukumba\n  Nkulukumba Tatana\n Wene u susaka\n  Wubihi bya misava.\n\n 4 - Hosi Yesu Kriste\n Hi yentxele wurombe\n Wene u susaka\n Wubihi bya misava.\n  Yingela ku kongota\n Ku kongota kweru\n  Wene u nga vokweni\n Dra xinene dra Tatana.\n\n 5 - Hosi Yesu Kriste\n Hi wene hakunene\n  wa ku xwenga\n Hi wene ntsena unga Hosi\n  U nga henhla-henhla\n  Ni Moya wa ku Xwenga\n A nfunwini wa Nkulukumba Tatana.", false),

                Cancao("34","Gloria","A twalisiwe nkulukumba, a henla","","Coro: A twalisiwe Nkulukumba, A henlha matilweni.\n  Ni ku rhula akuve misaveni Ka vanhu va ku rhandzeka.\n\n 1 - Ha ku dzunisa ha ku tlhangela,\n Ha ku gandzela ha ku twalisa\n  Ha ku khensa hi ku kwetsima.\n Ka Wena lo'ku kulu.\n\n 2 - Nkulukumba Hosi ya tilo,\n Nkulukumba wa ntamu hinkwawo\n  Hi we Hosi Yesu Kristo,\n  Nwana wa munwe wa Xikwembu.\n\n 3 - Hi we Yesu Xinyempfana,\n  Xa Nkulukumba Nwana wa Bava\n  U susaka xidjoho xa misava.\n  Hi twele wusiwana.\n\n 4 - Wena u susaka xidjoho xa misava,\n Yingisa ku khongela ka hina\n Wena U nga vokweni la xinene la Bava,\n Hi twele wusiwana.\n\n 5 - Hi Wena ntsena U nga Hosi.\n Hi Wena ntsena U nga Nkulukumba\n Hi Wena ntsena U nga Muhuluxi.\n  Hi Wena Yesu Kristo.\n\n 6 - Yesu Kristo ha kunene,\n U le henlha ngopfu swinene\n  Kunwe ni Moya wa ku kwetsima,\n  A nfunwini wa Nkulukumba.\n\n 7 - A dzuniwe Nkulukumba Bava.\n A dzuniwe Nkulukumba Nwana\n  Kunwe ni Moya wa ku kwetsima.\n  Hi malembe ni malembe", false),

                Cancao("35","Gloria","A twalisiwe nkulukumba aleluya","","Solista: A twalisiw\n\n Todos: Nkulukumba aleluya\n  Henhla matilweni,\n  Aleluya, aleluya\n Ni la misaveni\n  Aleluya, aleluya\n A ku ve ni ku rhula\n Aleluya, aleluya\n  Ka vanhu vo rhandzeka\n  Aleluya, aleluya\n\n Coro: Yimbelelani\n Yimbelelani, aleluya, (4x) (bis\n\n 2 - A twalisiwe\n  Nkulukumba, aleluya\n  Hosi ya matilo\n Nkulukumba Bava\n Wa ntamu hinkwaw\n\n 3 - A twalisiwe\n  Nkulukumba, aleluya\n  Hina ha ku dzunisa\n Hina ha ku tlangela\n Hina ha ku gandzela\n Hina ha ku twalis\n\n 4 - A twalisiwe\n  Nkulukumba, aleluya\n  Hina ha ku kensa\n Hi ku Kwetsima\n  Ka Wena lo'kukulu\n  Kukulu swinene\n\n 5 - Hosi Yesu Kristo\n Nwana wa Xikwembu\n Wena Xinyempfana\n  Xa Nkulukumba Bava\n\n 6 - Wena U susaka\n  Swidjoho swa misava\n  Hi twele wusiwana\n  U va U yamukela\n  Swikhongelo swa hina\n\n 7 - Wena U nga vokweni\n La xinene la Bava\n Hi twele wusiwana\n Hi timpswalo ta Wen\n\n 8 - Hi Wena wo Kwetsima\n  Hi Wena U nga Hosi\n Hi Wena U nga henhla\n  Jesu Kristo, aleluya\n Ni Moya wo Kwetsima\n A Nfunwini wa Bava.", false),

                Cancao("36","Gloria","A twalisiwe nkulukumba bava","","SOL: A twalisiwe\n\n TOD: A twalisiwe Nkulukumba Bava Aleluia, Aleluia\n\n SOL:A twalisiwe\n\n TOD: Hi Yena Nwana ni Moya wo Kwetsima, Aleluia, Aleluia\n\n 1. A twalisiwe Nkulukumba, aleluia.\n Henlha matilweni Nkulukumba,Aleluia,\n Ni la misaveni Nkulukumba Aleluia.\n A ku ve hi ku rhula Nkulukumba, Aleluia\n  Ka vanhu vo rhandzeka Nkulukumba,Alelui\n\n 2. Hosi ya matilo Nkulukumba, Aleluia\n Wa ntamu hinkwawo Hina ha ku khensa\n  Hina ha ku dzunisa Hina ha ku gandzela Hina ha ku khensa\n\n 3. Hina ha ku khensa hi ku kwetsima\n Ka Wena lo'kukulu kukulu swinene\n Hosi Yesu Kristo Nwana wa Xikwembu\n\n 4. Wena Xinyenpfana Xa Nkulukumba Bava\n  Wena u susaka swidjoho swa misava\n Hi tweli wusiwana u va u yamukela Swikhongelo swa hina\n\n 5. Wena u nga vokweni la xinene la Bava hi twele wusiwana\n Ni Moya wa ku kwetsima a nfunwini wa Bava.", false),

                Cancao("37","Gloria","A twalisiwe nkulukumba (23)","","Coro: A twalisiwe Nkulukumba matilweni\n ka vanhu va ku rhandzeka (bis)\n\n Nkulukumba Hosi ya matilo\n Nkulukumba Bava wa ntamu hinkwawo\n\n Ha ku dzunisa ha ku tlangela,\n Ha ku gandzela ha ku twalisa\n\n Ha ku khensa hi ku Kwetsima\n Ka Wena lo'kukulu\n\n Hi we Hosi Yesu Kristo\n  Nwana wa munwe wa Xikwembu\n\n Hi we Xinyempfana\n xa Nkulukumba Nwana wa Bava\n\n Wena U susaka swidjoho swa misava,\n  Hi twele wusiwana\n\n Wena U susaka swidjoho swa misava\n Yingisa a ku khongela ka hina\n\n Wena U nga vokweni la xinene\n  La Bava, hi twele wusiwan\n\n Yesu Kristo hi Wena hakunene\n  U nga wa ku Kwetsima.\n Hi Wena ntsena U nga Hosi.\n Hi Wena U nga henhla swinen\n\n Kunwe ni Moya wa ku Kwetsima\n  A Nfunwini wa Nkulukumba Bava. Ámen.", false),

                Cancao("38","Gloria","Há, há, há, a twalisiwe","","Coro: há, há, há (bis\n\n Sol.: a twalisiwe Nkulukumba\n  Bava, aleluia\n\n 1 - S : A twalisiwe; Aleluia há há\n  Nkulukumba Bava; Aleluia há há\n  Henhla matilweni; aleluia há há\n Ni la misaveni; aleluia há há\n  Akuve ni ku rhula; aleluia há há\n  Ka vanhu vo rhandeka: aleluia há há\n\n 2 - S : A twalisiwe; Aleluia há há\n  Nkulukumba Bava; Aleluia há há\n  Hosi ya matilo; aleluia há há\n Nkulukumba Bava; aleluia há há\n Wa ntamu hinkwawo; aleluia há há\n\n 3 - S : A twalisiwe; Aleluia há há\n   Nkulukumba Bava; Aleluia há há\n   Hina ha ku dzunisa; Aleluia há há\n  Hina ha ku tlangela; Aleluia há há\n \n Hina ha ku gandzela; Aleluia há há\n   Hina ha ku twalisa; Aleluia há há\n\n 4 - S : A twalisiwe; Aleluia há há\n   Nkulukumba Bava; Aleluia há há\n   Hina ha ku khensa; Aleluia há há\n   Hi ku kwetsima; Aleluia há há\n  Ka wena lo'kukulu; Aleluia há há\n  Ku kulu swinene; Aleluia há há\n\n 5 - S: A twalisiwe; Aleluia há há\n  Nkulukumba Bava; Aleluia há há\n  Hosi Yesu Kristu; Aleiuia há há\n   Nwana wa Xikwembu; Aleluia há há\n   Wena Xinyempfana; Aleluia há há\n  Xa Nkulukumba: Aleluia há há\n\n 6 - S: A twalisiwe; Aleluia há há\n  Nkulukumba Bava; Aleluia há há\n  Wena u susaka; Aleluia há há\n  Swidjoho swa misava; Aleluia há há\n  Hi twele wusiwana; Aleluia há há\n  U va u yamukela; Aleluia há há\n  Swikhongoto swa hina; Aleluia há há\n\n 7 - S: A twalisiwe; Aleluia há há\n  Nkulukumba Bava; Aleluia há há\n  Wena u nga vokweni; Aleluia há há\n   La xinene la Bava; Aleluia há há\n   Hi twele wusiwana; Aleluia há há\n   Hi timpswalo ta wena; Aleluia há há\n\n 8 - S: A twalisiwe; Aleluia há há\n  Nkulukumba Bava; Aleluia há há\n  Hi wena wo kwetsima; Aleluia há há\n Hi wena u nga Hosi; Aleluia há há\n  Hi wena u nga henhla; Aleluia há há\n   Yesu Kristu; Aleluia há há\n   Ni Moya wo Kwetsima; Aleluia há há\n   Nfunwini wa Bava; Aleluia há há", false),

                Cancao("39","Gloria","Glória, glória, glória","","CORO: Glória, glória, glória (3X).In excelsis Deo\n  Kutwala, kutwala, kutwala (3X) ku ve le matilweni\n\n 1 - A ku twa-a ku twalisiwe Nkulukumba\n   A henlha-a henlha matilweni\n  Ni misa-ni misaveni hinkwayo\n  Ka vanhu-ka vanhu vo rhandzeka\n  Ha ku dzu-ha ku dzunisa Bava\n  Ha ku tla-ha ku tlhangela Wena\n  Ha ku ga-ha ku gandzela hina\n  Hi wu ku-hi wukulu la Wena.\n\n 2 - Hosi Yesu - Hosi Yesu Kristo\n   Hi wena-Nwana wa Xikwembu\n  Hi wena-U nga Xinyempfana\n   Xa Nkulu-xa Nkulukumba Bava\n  Wena u-wena u nga vokweni\n   La xine-la xinene la Bava\n  Hi twele-hi twele wusiwana\n  Hi timpswa-hi timpswalo ta Wena\n\n 3 - Ha bonga-ha bonga ha twalisa\n   Hosi ya-Hosi ya matilo\n   Nkuluku-Nkulukumba Bava\n  Wa ntamu-Wa ntamu hinkwawo\n  Ha ku bo-ha ku bonga hi vana vaku\n   Ha ku twa-ha ku twalisa Bava\n   Hi minka-hi minkama hinkwayo\n   Hi la ku-Hi la ku nga heliki.\n\n 4 - Ha bonga-Ha bonga wunene laku\n  Hi nyiko-hi nyiko ya wutomi\n   Leyi te-Le'yi teke hi ka Kristo\n  Muhulu-Muhuluxi wa hina\n   Hosi rhu-Hosi rhumela Moya waku\n  Wu ta tsa-wu ta tsama na hina\n   Hi ta va-hi ta va la'vo basa\n   A timbi-A timbilwini ta hina.", false),

                Cancao("40","Gloria","Ku twala","","Coro: Ku twala , ku twala (2X) eh, eh, \n ku twala,(2x) ku twala (bis)\n\n 1 - Hosi Bava u nga matilweni...................Ku twala.\n Nkulukumba wa ntamu hinkwawo..........\n Ha ku dzunisa ha ku tlhangela......\n  Ha ku gandzela ha ku twalisa.....…\n\n 2 - Ku kwetsima ka Wena ku kulu\n Nkulukumba wa ntamu hinkwawo\n Ha ku dzunisa ha ku tlhangela\n  Ha ku gandzela ha ku twalisa\n\n 3 - U susaka wubihi la misava\n Hi yentxele a wurombe\n  Unga vokweni la Tatana\n  Hi yentxele a wurhombe\n\n 4 - Hi wena Hosi Yesu Kristo\n  Hi wena Nwana wa Xikwembu\n Xinyempfana xa Nkulukumba\n  Ha ku dzunisa ha ku tlhangela\n Ha ku gandzela ha ku twalisa\n\n 5 - Hi wena ntsena u nga Hosi\n Hi Wena u nga henlha-henlha\n  Kunwe ni moya wo kwetsima\n A nfunwini wa Nkulukumba Bava", false),

                Cancao("41","Gloria","Glória: ke twala a ku ve henhla","","Baixo: Ku twala\n Solo: Ku twala a ku ve ka Nkulukumba\n Baixo: Ku twala\n Solo: Ku twala a ku ve henhla, henhla\n\n 1 – Solistas: A ku rhula a ku ve kona\n Todos : Ku twala a ku ve ka Nkulukumba\n Solistas: A misaveni\n Todos : Ku twala a ku ve henhla-henhla\n Solistas: Ka lava randziweke\n Todos : Ku twala a ku ve ka Nkulukumba\n Solistas: Hi(i) Xikwembu\n Todos: Ku twala a ku ve henhla-henhla\n\n 2 - Nkulukumba Bava\n Hosi ya matilo\n Bava wa tamu hinkwawo\n  Ha ku dzunisa\n Ha ku tlangela\n Ha ku gandzela\n Ha ku twalisa\n  Ha ku khensa\n\n 3 - Hosi Yesu Kriste\n  Nwana wa Xikwembu\n Hosi Nkulukumba\n  Xinyenpfana xa\n  Nkulukumba\n  Hosi Nkulukumba\n Nwana wa Nkulukumba\n  Bava.\n\n 4 - Wena u susaka\n  Swidjoho swa misava\n   Hi twele wusiwana(2x)\n  Wena u susaka\n   Swidjoho swa misava\n  Yingisa ku khongela\n   Ku khongela ka hina\n  Wene u nga vokweni\n  La xinene la Bava\n   Hi twele wusiwana(2x)\n\n 5 - Hosi Yesu Kriste\n   Hi wene ha kunene\n  U nga wa ku kwetsima(2x)\n  Hi wene ntsena\n  U nga Hosi\n  U nga henhla-henhla\n   U nga henhla swinene\n   Kunwe ni moya\n  Wa ku kwetsima\n  Nfumwini wa Nkulukumba\n  Wa Nkulukumba Bava.", false),

                Cancao("42","Gloria","K u twala ku ve ka nkulukumba","","Coro: Ku twala ku ve ka Nkulukumba.\n  Ku twala ku ve a matilweni\n  Ni ku rhula a misaveni.\n   A vanwini vakwe (bis)\n\n 1 - Hina ha ku tlangela, hina ha ku twalisa.\n   Hina ha ku gandzela, hina ha ku dzunisa\n  Hosi Nkulukumba, Hosi ya matilo,\n  Nkulukumba Bava wa ntamu\n\n 2 - Ha ku bonga Hosi ya tihosi.\n  Hosi Yesu Kriste, Nwana wa Xikwembu\n   Hosi Nkulukumba Xinyempfana xa Nkulukumba,\n   Nwana wa Nkulukuba Bava.\n\n 3 - Wena U susaka swidjoho swa misava.\n   Hi twele wusiwana\n  Wena U susaka swidjoho swa misava.\n  Yingisa ku khongela ka hina.\n\n 4 - Wena u nga vokweni la xinene la Bava.\n  Hi twele wusiwana\n   Yesu Kristo hi wena ha kunene.\n   U nga wa ku kwetsima\n\n 5 - Hi wena ntsena U nga Hosi.\n   Hi wena U nga henlha-henlha\n  Kunwe ni Moya wa ku kwetsima.\n   A nfunwini wa Nkulukumba Bava", false),

                Cancao("43","Gloria","Yimbelelani","","4a Voz: Yimbelelani 1a Voz: Yimbelelani Hosi txhayani (2x)\n   Tingoma hi ku tsaka\n\n Todos: Yimbelelani.\n\n 1 - A twalisiwe Yimbelelani\n  Nkulukumba Bava Yimbelelani\n   Hosi ya matilo Yimbelelani\n   Wa ntamu hinkwawu Yimbelelani\n  Hina ha ku dzunisa Yimbelelani\n  Hina ha ku tlangela Yimbelelani\n   Hina ha ku gandzela Yimbelelani\n  Hina ha ku twalisa Yimbelelani.\n\n 2 - Hina ha ku khensa Yimbelelani\n  Hi ku kwetsima Yimbelelani\n  Ka wena lo'kukulu Yimbelelani\n   Kukulu swinene Yimbelelani\n   Hosi Yesu Kriste Yimbelelani\n   Nwana wa Xikwembu Yimbelelani\n  Wena Xinyempfana Yimbelelani\n  Xa Nkulukumba Bava Yimbelelani\n\n 3 - Wena u susaka Yimbelelani\n  Swidjoho swa misava Yimbelelani\n   Hi twele wusiwana Yimbelelani\n  U va u yamukela Yimbelelani\n   Swikhongelo swa hina Yimbelelani\n   Wena u nga vokweni Yimbelelani\n   La xinene la Bava Yimbelelani\n  Hi timpswalo ta wena Yimbelelani\n\n 4 - A twalisiwe Yimbelelani\n Nkulukumba Bava Yimbelelani\n  Hi wena wo kwetsima Yimbelelani\n Hi wena u nga Hosi Yimbelelani\n Hi wena u nga henhla Yimbelelani\n Hosi Yesu Kristo Yimbelelani\n Ni Moya wo Kwetsima Yimbelelani\n  A nfunwini wa Bava Yimbelelani.", false),

                Cancao("44","Aclamacao","Aleluia (35)","","Coro: Aleluia (6 v.)\n\n 1. Wena U Nwana wa Xikwembu\n Wena U Nwana wa Hosi,\n  Aleluia, Ámen. (2 v.)\n\n 2 - A tintsumi ti ta kwela,\n A tintsumi ti ta xika,\n Aleluia, Ámen. (2 v.)\n\n 3 - Sekelekani Vamakwerhu.\n  Sekelekani mi twa Rito,\n Aleluia, Ámen. (2 v.)", false),

                Cancao("45","Aclamacao"," Aleluia a hi fambeni ka Yesu (36)","","Coro: Aleluia, aleluia (2)\n  A hi fambeni ka Yesu, Aleluia.\n\n 1 - Vakriste yingisani\n  Aleluia, a hi fambeni ka Yesu, aleluia\n  Wa vitana hinkwavo\n  Aleluia, a hi fambeni ka Yesu, Aleluia,\n\n 2 - A lava ku hi maha\n Aleluia a hi fambeni ka Yesu, Aleluia\n  tinhenha ta Evangeli\n  Aleluia, a hi fambeni ka Yesu, Aleluia.", false),

                Cancao("46","Aclamacao","Aleluia mu bongeni (37)","","1 - Aleluia mu bongeni\n lw'a ngeke na wugamu\n Aleluia, Mu bongeni\n  Muvumbi wa hinkwaswo.\n\n 2 - Aleluia, Mu bongeni\n Lw'a ngeke na wugamu\n a fanekela ku fuma\n matilo ni misava.\n\n Coro: Aleluia! Aleluia!\n  Aleluia Mu bongeni,\n Aleluia, Mu bongeni\n  Lw'a ngeke na wugamu.", false),

                Cancao("47","Aclamacao","Evangeli ya Hosi (45)","","Coro: Evangeli, Evangeli, Evangeli ya Hosi (2x)\n  Hi kanela na mina\n Hi kanela na hina\n  Wena na hina.", false),

                Cancao("48","Aclamacao","Hlanhla Hosi (38)","","1 - Hlanhla, hlanhla, Hosi:\n  Hlanhla mbilwini yanga.\n\n 2 - Rima, rima, Hosi;\n Rima mbilwini yanga\n\n 3 - Byala, byala, Hosi\n  byala mbilwini yanga.", false),

                Cancao("49","Aclamacao","HLAYA HOSI, HA KOMBELA (44)","","Aleluia, Aleluia, Aleluia\n  Aleluia, Aleluia, Aleluia, Aleluia ...\n  Hlaya Hosi, ha kombela, rito laku la wutomi\n vaninga timbilu terhu, aleluia.", false),

                Cancao("50","Aclamacao","Hosana aleluia (39)","","Sol: Hosana aleluia, aleluia (2x)\n hosana.\n Tod: Hosana, aleluia, aleluia (2x)\n\n 1 - Rito la Wena, Hosi i wutomi\n vula-vulani, Hosi ha yingisa.\n\n 2 - Rito la Wena, Hosi i swakudla\n vula-vulani, Hosi ha yingisa.", false),

                Cancao("51","Aclamacao","Ndri voni a xihlamaliso (40) (Ronga)","","1 - Ndri voni a xihlamaliso:\n  Ndri voni tilo le'drimprha\n  Ndri voni misava le'mimprha\n Na ndri li kola a misaven.\n\n Coro:Aleluya, Aleluya, Aleluya!\n Ku bongiwe Xikwembu. (bis)\n\n 2 - Ndri voni munti wa ku basa\n  A Yerusalema mumprha\n  Na a xika hi le tilweni\n Ne karhi a longisiwile.\n\n 3 - Ndri yingela rito dri huma\n   Dri ku: Hinkwenu ndrulukani\n  Nkulukumba wa mi lavisela\n   A ta mi pfumela hinkwenu.", false),

                Cancao("52","Aclamacao","Rito la Xikwembu (41)","","1 - Rito la Xikwembu i wutomi (2)\n  Li hanyisa vanhu la'va li lavaka\n  Rito la Xikwembu i wutomi.\n\n 2 - Lw'a nyenyaka Yesu a ta lova (3)\n   Kambe rito la Xikwembu i wutomi.\n\n 3 - Swi ni wusiwana la'va li nyenyaka (3)\n  Kambe Rito la Xikwembu i wutomi.\n\n 4 - Lw'a rhandzaka Yesu a ta hanya (3)\n   Kambe Rito la Xikwembu i wutomi.", false),

                Cancao("53","Aclamacao","Yingisani misava","(Após o evangelho)","SOL.: Yingisani misava\n TOD: Muhaxi a humile ku ya byala mbewu\n   ku ya byala mbewu.\n SOL.: Yinwani\n TOD.: Yiwa le ndleleni yiwa le ribyeni, yiwa le mintweni\n yi kamanyetiwa, yinwani yi wa le nsinweni yo nona (2x)\n SOL.: Tana rito la Hosi\n TOD: Tana uta hi ponisa (2x)\n TOD : Nkateko wa Hosi, xika henhla ka mina\n Ndzi ta va nsimu leyo nona. (2x)", false),

                Cancao("54","Aclamacao","Ndza pfumela",""," - Ndza pfumela ka Nkulukumba\n  Bava wa ntamu hinkwawu,\n   mutubuluxi wa tilo ni misava\n   ni wa mintxhumu hinkwayo\n   le yaku voneka ni le yinga vonekike. (2v)\n\n 2 - Ndza pfumela ka Hosi yinwe yena Yesu Kriste\n  Nwana wa munwe wa Xikwembu\n  A nga pswaliwa hi Bava,\n   na ku nga se va na masangulo.\n\n 3 - Yena a nga Nkulukumba e ka Nkulukumba,\n   livoningo e ka livoningo\n   Nkulukumba munene e ka Nkulukumba munene.\n  a pswaliwile a nga tumbuluxiwanga:\n  A ni ntumbuluko wunwe na Bava,\n  hi yena a nga tumbuluxa mintxhumu hinkwayo.\n\n 4 - A xike hi le tilweni,\n  hi mhaka ya hina vanhu ni ku ponisiwa ka hina .\n   a hikiwile a khwirhini la nhwanyana Maria,\n   hi ntamu wa moya wa ku kwetsima\n  a tinyentxa munhu.\n\n 5 - A vambiwile a Xihambanwini hi mhaka ya hina\n  a nkameni wa Poncio Pilato,\n   A xanisekile, a fa, a lahliwa .\n  A pfuke kufeni hi siku la wunharhu,\n   tani hi ku vula ka matsalwa,\n   a tlhatuke tilweni, a ya tsama a vokweni\n   la xinene la Bava.\n\n 6 - A ta thlela a vuya hi ku kwetsima a ta yavanyisa,\n  la va ku hanya ni la’va kufa.\n   A nfumu wa yena u nga ta va na wugamu.\n   Ndza Pfumela ka moya wa ku kwetsima,\n   Hosi le’yi hanyisaka,\n  Lw ́a humaka ka Bava ni ka Nwana\n   Lw ́a gandzeliwaka ni ku tlangeliwa\n   kunwe na Bava ni Nwana\n   lw ́a anga vula-vula hi vaprofeta.\n\n 7 - Ndza pfumela ka bandla linwe\n   la kuhlawuleka la katólika.\n  Ndza pfumela ku tsakamisa kunwe\n   Lo’ku basisaka swidjoho.\n   Ndzi rindzela ku pfuka ka vafi\n   ni wutomi la nkama lo’wu taka. Amem.", false),

                Cancao("55","Aclamacao","Ndza pfumela ka nkulukumba","","Coro: Ndza pfumela ka Nkulukmba,\n  Mutumbuluxi wa hinkwaswo\n  Ndza pfumela ka Yesu Kristo.\n  Ndza pfumela ka Moya wo kwetsima\n\n 1 - A nga tumbuluxa, a tilo ni misava\n Na Yesu Kristo Nwana wakwe Hosi ya hina ya yinwe.\n\n 2 - A nga hikiwa hi ntamu wa Moya wa ku kwetsima\n  A pswaliwa hi Maria wa ku xwenga\n\n 3 - A xanisekile, a nkameni wa Poncio Pilato\n  A vambiwa Xihambanwini a file a lalhiwa.\n\n 4 - A lalhiwile a sirheni, hi siku la wunharhu.\n A pfukile a kufeni a pfukile a kufeni.\n\n 5 - A tlhatukela a tilweni, a ya tshama a vokweni la xinene la\n  Bava Mutumbuluxi wa hinkwaswo.\n\n 6 - A ta tlhela a vuya. Hi ku kwetsima lo'kukulu.\n A ta yavanyisa a nfumu wakwe a wu gami.\n\n 7 - Ndza pfumela ka Moya wa ku kwetsima\n Ni ka bandla la ku lhawuleka la Katolika,\n  Ndza pfumela ku pfuka ka vafi, ni ka wutomi le'li taka. Ámen.", false),

                Cancao("56","Aclamacao","Ndza pfumela ka nkulukumba bava","","1 - Ndza pfumela ka Xikwembu Bava, ndza pfumela\n Mutumbuluxi wa tilo ni misava, ndza pfumela\n  Ni wa hinkwaswo le'swi nga ka Kona, ndza pfumela\n  Ndza pfumela mina ndza pfumela; ndza pfumela.\n Tekel. Pfumela-ku nkulukumba mun'we\n  Pfumela mina-ndza pfumela (bis)\n\n 2 - Ndza pfumela Yesu, Muhuluxi, ndza pfumela\n Yena Nwana wa Xikwembu, Bava ....\n  A nga velekiwa hi Maria...\n  Ndza pfumela, mina ndza pfumela ...\n\n 3 -Ndza pfumela Yesu Muvambiwa..\n  A nga hi fela a xihambanwini...\n Ku fela swidjoho swa hinkwerhu\n Ndza pfumela, mina ndza pfumela ...\n\n 4 -Ndza pfumela ka Yesu Mupfuki\n A hlulile lifo hi matimba...\n A thlantukelile a tilweni...\n Ndza pfumela, mina ndza pfumela…\n\n 5 - Ndza pfumela ka Moya wo Basa...\n Hosi le'yi hanyisaka vhanu\n Yi huma ka Bava ni ka Nwana..\n  Ndza pfumela, mina ndza pfumela...\n\n 6 - Ndza pfumela kereke ya Yinwe...\n Yona kereke ya Katolika..\n  Ni ku twanana ka vahlawuleki...\n Ndza pfumela, mina ndza pfumela ...\n\n 7 - Ndza pfumela ku pfuka ka vafi...\n  Ndza pfumela wutomi le'limpswa...\n Wutomi la limpswa le'li taka...\n  Ndza pfumela, mina ndza pfumela...", false),

                Cancao("57","Aclamacao","Xana ma pfumela-kê (48)","","Ndza pfumela ka Nkulukumba wa munwe ntsena,\n  ndza pfumela.\n Muwumbi wa tilo ni misava? Ndza pfumela.\n Ma pfumela kê? Ndza pfumela.\n\n 1 - Xana ma pfumela ka Nkulukumba Bava wa ntamu\n hinkwawo\n Muwumbi wa tilo ni misava xana?\n\n 2 - Ma pfumela ka Yesu Kristu Nwana wa munwe wa\n Xikwembu xana?\n Lwe'a velekiweke hi Nhwanyana Maria/A pfukeke hi\n siku la wunharhu a tlhatukela Tilweni xana?\n\n 3 - Ma pfumela ka Moya wo Kwetsima\n  Ni ka Kereke yo hlawuleka ya Katolika xana?\n\n 4 - Ka nthangano wa vahlawuleki\n Ni ku rivaleliwa ka swidjoho\n ku pfuka ka vafi ni wutomi li nga heliki xana?", false),

                Cancao("58","Ofertorio","A hi fambeni (65)","","Coro: A hi fambeni, aleluia (2x)\n  Hi ya, hi ya nyikela (2x)\n Mhamba, aleluia (2x)\n\n 1 - Hi famba ni kati (2x)\n\n 2 - Hi famba ni vinya (2x)\n\n 3 - Hi famba hinkwerhu\n\n 4 - Hi famba hi ku nyonxa\n\n 5 - Hi famba hi mbilu\n\n 6 - Hi famba ni miri\n\n 7 - Hi famba ni hinkwaswo\n\n 8 - Hi famba ni male\n\n 9 - Hi famba ni majovem\n\n 10 - Hi famba hinkwerhu", false),

                Cancao("59","Ofertorio","A xisiwana xanga hi mini (64) (RONGA)","","1 - A xisiwana xanga hi mine\n Xi ta ku nyika yini xana Hosi yanga\n  Xana ni mani, We Hosi yanga,\n  Ku va mahlweni kwaku Hosi ya tihosi.\n\n Coro: Yamukela, Hosi, a mbilu yanga\n  Yamukela, Hosi, a moya wanga\n  Yamukela lirhandro dranga\n Yamukela wutomi branga (bis).\n\n 2 - Ku ndzi hlawula, we Hosi yanga\n  U yo vona yini xana, Hosi ya wumbilu\n  A ndzi na ntxumu ku ta khensa\n Ndzi yamukele, Wena Hosi ya wurombe.\n\n 3 - Hi ledri kati, ku nyonxa kwanga\n Hi ledri vinya dra huluxo ni wutomi\n  Mintiro yanga, ku hanya kwanga\n  Ndzi yamukele, Wena Hosi wurombe.", false),

                Cancao("60","Ofertorio","Bava wa matilo (50)","","1 - Bava wa matilo, yamukela\n A swihanano, yamukela.\n\n Coro: Bava, yamukela (4)\n\n 2 - Bava wa matilo, yamukela\n  A xinkwa xa wutomi, yamukela.\n\n 3 - Bava wa matilo, yamukela\n  A vinya la wutomi, yamukela.", false),

                Cancao("61","Ofertorio","Ha buya ni mhamba yeru (Ronga)","","1 - Ha buya ni mhamba yeru\n Yamukela bava\n  Yamukela bava\n Yamukela bava.\n\n Coro: yamukela bava,\n  yamukela bava. (4x)\n\n 2 - Ha buya ni kati dreru   - yamukela bava\n\n 3 -  Ha buya ni vinya dreru   - yamkela bava\n\n 4 - Ha buya ni mihandru yeru - yamukela bava\n\n 5 - Ha buya ni male yeru - yamukela bava\n\n 6 -  Ha buya ni mutxhatu weru - yamukela bava\n\n 7 - Yamukela mintiru yeru - yamukela bava\n\n 8 - Yamukela ku nyonxa kweru - yamukela bava\n\n 9 - Yamukela ti mbilu teru - yamukela bava\n\n 10 - Yamukela lirandru dreru - yamukela bava", false),

                Cancao("62","Ofertorio","Ha vuya nayo mhamba","","Solistas: Coro: Ha vuya nayo mhamba (3x)\n Todos: Ha vuya nayo mhamba\n Hosi, hi ta nyikela ka Wena.\n\n Solistas: Hi ntamu wa wena Hosi.\n\n 4a Voz: - Vapapai va vuya...!\n Vamamani va vuya....\n Majovem ma vuya...\n A Mutxhatu wa vuya...\n  A vatsongwana va vuya....\n  Hinkwerhu ha vuya.…", false),

                Cancao("63","Ofertorio","Hi leyo mhamba","","SOL:.  Hi leyo mhamba yo gandzela Wena (2X)\n TOD: Yi yamukele yi katekise\n  SOL: Hum,Hum yamukela Hosi\n  TOD: Yi yamukele yi katekise\n  Coro:  Yi yamukele (8x)\n Yi katekise.", false),

                Cancao("64","Ofertorio","Hi yamukele nkulukumba (51)","","Coro: Hi yamukele, Nkulukumba.  1 - Hi vanhu la'vo bina.\n  2 - Ha tisola mahlweni ya Wena.\n 3 - Hi ponise, Hosi Yesu.\n  4 - Yingisa mikombelo ya hina.\n  5 - Ha ku luva hi xinkwa.\n 6 - Hi xinwelo xi nga ni vinya.\n  7 - Katekisa swona, Hosi.\n 8 - Swi va Miri ni Ngati ya Wena.", false),

                Cancao("65","Ofertorio","Hi yoleyo mhamba YOLEYO MHAMBA","","Sol: Hi yoleyo mhamba yo Gandzela Wena(2x)\n Tod: Yi yamukele yi katekise\n Sol: Wena, Yamukela Hosi\n Tod: Yi yamukele yi katekise\n Coro: Sol.: Yi Yamukele Yi Y\n  amukele(4x)\n Tod.: Yi Yamukele Yi Yamukele.\n\n 2 - Xinkwa ni vinya hi ta nyika Wena.\n Swi yamukele swi katekise.\n\n 3 - Mali kutsaka hi ta nyika Wena\n Swi yanukele swi katekise.\n\n 4 - Hi ta ni vana hi ta nyika Wena\n  Va yamukele va katekise.", false),

                Cancao("66","Ofertorio","Hina ha vuya (52)","","Coro: Hina ha vuya la' ka Hosi hi ta nyikela\n  Mhamba ya hina ni wusiwana hi ta nyikela (2)x.\n\n 1 - Xinkwa ni vinha hi nga naswo hi ta nyikela\n  mbilu ni moya wa hina hi ta nyikela.\n\n 2 - Xinkwa ni vinha hi nga naswo, hi ta nyikela\n wutomi la hina ni lirhandzu, hi ta nyikela.\n\n 3 - Xinkwa ni vinya hi nga naswo, hi ta nyikela\n kutsaka ka hina ka siku leli, hi ta nyikela.\n\n 4 - Xinkwa ni vinya hi nga naswo, hi ta nyikela\n mihandzu ya hina hi nga nayo, hi ta nyikela.\n\n 5 - Xinkwa ni vinya hi nga naswo, hi ta nyikela\n Kereke ya wena yi kulaku, hi ta nyikela.", false),

                Cancao("67","Ofertorio","Hina ha vuya ni minyikelo",""," Coro: Hina ha vuya ni minyikelo\n Mhamba ya hina yi hamukele. 2x\n\n 4a voz: Ha vuya hina ha vuya.\n\n 1 - xinkwa hi lexi,\n vinya hi leli,\n hi leyi mhamba.\n\n 2 - Mbilu hi leyi,\n  Moya hi lowu,\n  hi leyi mhamba.\n\n 3 - vana hi lava,\n  kereke hi leyi,\n  hi leyi mhamba.\n\n 4 - Male hi leyi,\n tiko hi leli,\n  Hi leyi mhamba.\n\n 5 - Ntsako hi lowu,\n hinkwaswo hi leswi,\n  hi leyi mhamba.", false),

                Cancao("68","Ofertorio","Hosi hi leyi minyikelo yerhu (53)","","Coro: Hosi hi leyi (2v)\n Minyikelo Yerhu (bis)\n\n 1 - Yamukela a xinkwa      minyikelo yerhu\n  Yamukela a vinya		‘	   ‘\n  Swi hundzule We Bava	‘	   ‘\n  Swi va Miri ni Ngati		‘	   ‘\n\n 2 - Ntshovelo wa masimu 	‘	   ‘\n  Ni mihandzu hinkwayo	‘	   ‘\n  Ha ku nyika nyamuntlha ‘	   ‘\n  Yamukela We Hosi. ‘	   ‘\n\n 3 - A mintirho ya hina ‘	   ‘\n  A mindjangu ya hina ‘	   ‘\n  A vana va hina ‘	   ‘\n  A wutomi la hina ‘	   ‘\n\n 4 - Yamukela timbilu ‘	   ‘\n  Ta vana va Wena ‘	   ‘\n  U ti tata hi Moya ‘	   ‘\n  A kuva ti ku tiva. ‘	   ‘", false),

                Cancao("69","Ofertorio","Hosi mine ndri xisiwana (66) (RONGA)","","Coro: Hosi mine ndri xisiwana\n  A ndri na xa lisima xa ku ndri nga ku nyika\n kambe hi la ka wurombe braku\n yamukela mine, we Hosi.\n\n 1 - Wutomi ledri u ndri nyikiki\n nyamuntlha ndraku bekela Hosi\n  A moya ni mbilu ni hinkwaswo\n  Yamukela mine we Hosi.\n\n 2 - A ntamu lowu u ndri nyikiki\n A miri ni mbilu yanga, Hosi\n  A swi ve swa ku twalise Wene\n  Yamikela wusiwana branga.\n\n 3 - Mintiro ni minkaralo yanga\n  loko mi nge na lirandru draku\n a mi pfuni ntxhumu le ku Wene\n  yamukela wusiwana branga.\n\n 4 - Tatana langusa hi vumbilu\n vamakweru la'va hluphekaka\n va djulaka ndlela ya vutomi\n  yamukela wusiwana branga.", false),

                Cancao("70","Ofertorio","Hosi vona (54)","","1 - Hosi, vona, hi fika hi ku tsaka (2v.)\n Ha ku tisela tinyiko. (2 v.)\n\n 2 - Hosi, vona, hi fika hi ku tsaka (2 v.)\n  Ha ku tisela xinkwa. (2 v.)\n\n 3 - Hosi, vona, hi fika hi ku tsaka (2 v.)\n  Ha ku tisela vinya. (2 v.)\n\n 4 - Hosi, vona, hi fika hi ku tsaka (2 v.)\n  Ha ku tisela timbilu. (2 v.)", false),

                Cancao("71","Ofertorio","Hosi yamukela swihanano swa hina (55)","","1 - Hosi, yamukela swihanano swa hina. (x2)\n\n Coro: Hosi! Yamukela (x3) Hosi!\n Yamukela swihanano swa hina.\n\n 2 - Hosi yamukela a xinkwa xa hina.\n Yamukela swihanano swa hina. (x2)\n\n 3 - Hosi yamukela a vinya la hina.\n  Yamukela swihanano swa hina. (x2)\n\n 4 - Hosi yamukela ku tsaka ka hina.\n Yamukela ku tsaka ka hina. (x2)", false),

                Cancao("72","Ofertorio","Mayiwako loko inge Hosi (Ronga)","","1 - Mayiwaku loko inge Hosi, a ndzo fela ka Wena\n  Ndzi hetisa swi rhumo swaku, utonwini dranga Hosi\n\n Coro: Yamukela muvumbi wanga a ka bonga ka mina,\n  Ndzi vuysa wutomi byanga le malhweni ka wena (bis)\n\n 2 - Yamukela fulaho leyi, li hundzule miri waku\n  Ni vinya u li hundzula li va ngati yaku ,Hosi.\n\n 3 - Misava, Hosi , mi tsakamile hi ngati ya vanaku, Hosi,\n  Miri wavo wu yavanile: ndzi swi viyisa ka wena.\n\n 4 - Tatana oh! Yamukela ni swirilo swa hina\n  Matlhari ma songa tiko, swi hundzuli ku rhula Hosi\n\n 5 - Yamukela xirilo xanga ni xitsungu xaku Hosi\n  Ni hinkwavu va nga malhweni, ndzi va vuyisa gandzelweni", false),

                Cancao("73","Ofertorio","Mbilu yanga (56)","","1 - Mbilu yanga hi huma le ka Hosi;\n Ndzi ta thlelisa ka Yona.\n\n Coro: Ndzi ta tsaka, ndzi tsakisisa\n  Loko ndzi ta fika le ka Hosi;\n Ndzi ku: Hosi, leswi U nga ndzi nyika\n  Ndzi thlelisile ka Wena.\n\n 2 - Moya wanga wu huma le ka Hosi;\n  Ndzi ta thlelisa ka Yona.\n\n 3 - Male yanga yi huma le ka Hosi;\n  Ndzi ta thlelisa ka Yona.\n\n 4 - Le'swinyingi swi huma le ka Hosi;\n Ndzi ta thlelisa ka Yona.\n\n 5 - Le'switsongo swi huma le ka Hosi;\n Ndzi ta thlelisa ka Yona.\n\n 6.Ni hinkwaswo swi huma le ka Hosi;\n Ndzi ta thlelisa ka Yona.", false),

                Cancao("74","Ofertorio","Mhamba ya ku luva wena (Ronga)","","Coro: Yamukela: hi yona mhamba\n  ya ku luva wena (2 v)\n\n 1 - Hi leyi mhamba yo luva wena\n Hosi yamukela xihiwa lexi\n  Hi leyi mhamba yo luva wena\n Hosi yamukela vana va wena\n\n 2 -  Hi lexi Xinkwa ndzuluta mirhi\n  Hosi hi xurhisi hi mirhi waku\n Hi leli vinya, ndzuluta Ngati\n  Hosi hi basisi hi ngati leyi.\n\n 3 - Hi leyi kereke, hileyi yindlo ya wena\n Hosi katekisa thepele leli\n Hi lava varisi, hi lawa malandza yaku\n  Hosi katekisa vana va wena.\n\n 4 - Hi leyi mihandzu, hi lowu ntirho wa hina\n  Hosi ha ku nyika na hi tsakile.\n Hi leyi mindjango, ni mixuvo ya yona\n Hosi katekisa tiko la wena.\n\n 5 - Hi loku kutshembha ka vana vaku\n Hosi hi tiyisi hi moya waku\n  Hi leli Bandla la vapfumeli,\n  Hosi katekisa vana va wena.", false),

                Cancao("75","Ofertorio","Ndri ta tinyikela hosini (68) (Ronga)","","1 - Ndri ta tinyikela Hosini\n Ndri nyika hinkwaswo\n Mihefemulo ni mbilu,\n\n Miri wanga hinkwawo.\n  A mandla, minkondro lidrimi\n swi ta va swa Muhanyisi\n  Yesu a ta trama mbilwini\n  Ndri hanyela Yene.\n\n 2 - Ndri ta fambisa Evangeli\n  Ndri koka ka Yesu\n La'va trhamaka munyameni\n ku djula va mu tiva;\n Vahoni, vavabri, vamatiko\n  Yesu a ti ka hina:\n  Fambani mu va tisa ka Mina\n  Va ta hanyisiwa", false),

                Cancao("76","Ofertorio","Ndzi ta humexa a mhamba (57)","","1 - Ndzi ta humexa mhamba yanga,\n  Ndzi gandzela Hosi yanga\n  Lw'a tiki la misaveni a ta ndzi huluxa\n  Ndzi ta nyika na ndzi nga feleli.\n\n Coro:\n Yamukela, Hosi, mhamba yanga,\n  Hambi yi li yitsongo, Hosi yanga,\n Ndzi nyika swa wusiwana byanga;\n  Le'switsongo ndzi nyika hi mbilu.\n\n 2 - Mawaku loko, we Hosi yanga,\n U yamukela mhamba leyi\n Yi nga mhamba yaku khensa timpswalo taku;\n  Ndzi ta nyika na ndzi nga feleli.\n\n 3 - Ndza ku kombela, we Hosi yanga,\n U yandzisa a mhamba leyi;\n  Yi va le'yi kulisaka a ntirho waku,\n A Kereke li yandza misaveni.", false),

                Cancao("77","Ofertorio","Oh, oh, yamukela (58)","","Coro: Oh yamukela a mhamba ya hina hi ku nyikaka,\n  A siku la nyamuntlha\n A hi na yinwani mhamba hi ku tsakisaka\n  A handle ka yoleyi.\n\n 1 - A xinkwa xa hina, ni vinya la hina\n   ha ku nyika, Bava.\n\n 2 - Ku rhila ka hina, ku tsaka ka hina,\n  ha ku nyika, Bava.\n\n 3 - Wutsongo la hina, wunyingi la hina\n   ha ku nyika, Bava.\n\n 4 - Wutomi la hina, nhlomulo wa hina,\n  ha ku nyika, Bava.\n\n 5 - Wunene la hina, wubihi la hina,\n  ha ku nyika, Bava.", false),

                Cancao("78","Ofertorio","Yamukela Hosi (60)",""," Coro: Yamukela, Hosi\n  Swihanano swerhu\n Katekisa, Hosi,\n   Minyikelo yerhu.\n\n 1 - Hosi, yamukela\n   Minyikelo yerhu;\n   Hi ku tiselaka\n   Nyamunthla.\n\n 2 - A hi na le'swinyingi\n   Kambe yamukela\n   Xikombiso xa miri\n  Ni ngati.\n\n 3 - Yamukela ni timbilu\n  Ta vana vaku;\n  U ti tata hi Moya\n Wa wena\n\n 4 - Ha ku nyiketa, Hosi\n  la'va nga ku tiviki\n  U va pfula timbilu\n  Ta vona.", false),

                Cancao("79","Ofertorio","Yamukela Hosi (69) (Ronga)",""," Coro: Yamukela, Hosi, Mhamba\n   hi ku nyikaka, hina vadjohi (2x).\n\n 1 - Lexi i Xinkwa (4x)\n\n 2 - Leli i vinya\n\n 3 - Leyi i male\n\n 4 - Wusiwana dzezu (4x)\n\n 5 - Mitsovelo yerhu (4x)\n\n 6 - Kutsaka kwerhu", false),

                Cancao("80","Ofertorio","Yamukela wena Hosi (62)",""," Coro: Yamukela, Wena Hosi,\n  swihanano swerhu\n   Yamukela wena Hosi.\n\n 1 - A Xinkwa ni lo Vinya\n  Yamukela, Hosi.\n  Swi hundzule Tatana,\n   Swi va Miri wa Wena.\n\n 2 - A mintirho ni lirhandzu\n   yamukela, Hosi\n  Ha ku nyika nyamuntlha\n   Yamukela, Wena Hosi.\n\n 3 - A ku tsaka ni ku rila\n  yamukela, Hosi.\n  Katekisa, nyamuntlha\n  hina, vana va Wena.", false),

                Cancao("81","Elevacao","Ndzi vonile Hosi (74)","","Ndzi vonile Hosi, yi tshame thempeleni\n  Tintsumi ti yimba hi tinanga\n Ti ku: kunene U Kwetsimile\n  Hosi ya mavandlha.\n\n Coro: U Kwetsimile (7 vezes)\n  Hosi ya mavandlha.", false),

                Cancao("82","Elevacao","U kwetsimile Hosi (77)","","Coro: U Kwetsimile Hosi (3)\n\n U Kwetsimile, U kwetsimile, Hosi.\n\n 1. Solo: Nkulukumba Hosi\n  Todos: Ya swilo hinkwaswo (2)\n  Solo: A tilo ni misava\n  Todos: Swi talile (2)\n Solo: Hi mintirho ya Wena\n   Todos: Swi talile hi mintirho ya Wena.\n\n 2. Solo: A katekile\n   Todos: Lweyi a taka (2)\n  Solo: Hi vito la Xikwembu\n  Todos: La Xikwembu (2)\n  Solo: Hossana, Hossana\n  Todos: Hossana, Hossana le matilweni.", false),

                Cancao("83","Elevacao","U kwetsimile Hosi hosana","","U kwetsimile Hosi Hosana...Hosana, Hosana\n   Nkulukumba Bava Hosana...\n   A tilo ni misava Hosana...\n   wi tali hi nfumu waku, Hosana ....\n\n Coro: Hosana, Hosana, Hosana, Hosana\n   Hosana, Hosana, Hosana, Hosana.(2x)\n  A katekile lweyi a taka Hosana.....\n  Hi vito la Wena Hosana ....\n Nkulukumba Bava Hosana.....\n   Hosana matilweni Hosana...", false),

                Cancao("84","Elevacao","U kwetsimile we Hosi (78)","","Xikwembu nwinyi wa hinkwaswu\n  tilo ni misava swi talile\n  hi nfumu waku.\n  U katekile Wena u taka\n  hi vito la Hosi\n Hossana le matilweni\n\n Coro: U kwetsimile\n  U kwetsimile nwe Hosi\n  Le henhla tilweni.", false),

                Cancao("85","Elevacao","U kwetsimile, Hosi, Hosi","","SOL: U kwetsimle,\n TOD: U kwetsimile, Hosi,Hosi Xikwembu (bis)\n SOL:Tilo ni misava,\n TOD: Tilo ni misava swinkwe, swi kwetsimile\n\n REFRÃO\n SOL:Hosana, Hosana (bis)", false),

                Cancao("86","Elevacao","U kwetsimile (76)","","Coro: U Kwetsimile, U Kwetsimile\n  U Kwetsimile Hosi ya tilo. (2x)\n A tilo ni misava swi kombisa wukulu la Wena.\n Hossana le matilweni.\n\n Hossana, Hossana\n  Hosana le matilweni.\n  Ha ku khensa, Hosi ya tilo\n  Nkulukumba wa matimba, Hosi ya tilo,\n  Muvumbi wa hinkwaswo, Hosi ya tilo.\n Hossana, Hosana\n  Hossana le matilweni.", false),

                Cancao("87","Elevacao","U xwengile (79) (Ronga)","","Coro: U Xwengile, U Xwengile, we Nkulukumba\n U Xwengile, U Xwengile, Nkulukumba.\n\n 1 - Hinkwaswo swivumbiwa\n  Swi twalisa vito draku\n  A tilo ni misava\n  Swi korhama mahlwen kwako.\n\n 2 - Hosana a henhla henhla\n  A katekile l'wa taka hi vito\n  dra Nkulukumba.", false),

                Cancao("88","Elevacao","Wo basa",""," Wo Basa, i Hosi Xikwembu xa tilo Hosana (bis)\n\n CORO: BAIXO. Ha yimba\n TOD: Yimba Hosana (bis).\n Ni tintsumi ti ta yimba matilweni Hosana.\n\n Ha ku khensa, we Hosi, Muvumbi wa hinkwaswo Hosana (bis)\n SOL:U Xwengile (3X)\n TOD.:U Xwengile (3X) U xwengile Hosi,\n\n A tilo ni misava swi talile hi nfumu waku\n Hosana, Hosana, Hosana matilweni (bis)\n\n A Katekile Lweyi a taka hi Vito la Hosi\n Hosana, Hosana, Hosana, matilweni (bis)", false),

                Cancao("89","Elevacao","Hosi yanga","","Hosi yanga ni Xikwembu xanga\n  Ndza ku pfumela, ndza ku gandzela.", false),

                Cancao("90","PaiNosso","Bava wa hina (81)","","Coro: Bava wa hina u nga matilweni\n  Vito la Wena a li twalisiwe\n A wu te ka hina a nfumu wa Wena\n  Ku rhandza ka Wena a ku yentxiwe.\n\n - A misaveni tani hi le tilweni\n - Ku rhandza ka Wena a ku yentxiwe\n - Hi nyike nyamunthla swakudla swa hina - Ku rhandza ...\n - Swakudla swa hina swa siku ni siku - Ku rhandza ...\n\n -Hi rivalele swidjoho swa hina - Ku rhandza ...\n - Tani hi leswi na hina - Ku rhandza ...\n - Hi rivalelaka la'va hi djohelaka - Ku rhandza …\n\n - U nga hi tshiki hi wa miringweni - Ku rhandza ...\n - Kambe hi ponise ka le'swakubiha - Ku rhandza ...", false),

                Cancao("91","PaiNosso","Hi rivalele Hosi ","","Coro: Hi rivalele Hosi\n  Yesu Hosi\n Hi rivalele Hosi\n A swidjoho swa hina.\n Bava wa hina, u nga matilweni\n  Vito la Wena a li twalisiwe (2x)\n  Bava u nga matilweni.\n\n 2 - Mfumu wa wena, a wu te ka hina\n  Ku randza ka wena a ku yentxiwe\n Bava u nga matilweni.\n\n 3 - A tilweni ni la misaveni\n  Hi nyike nyamunthla, swakudla swa hina\n  Bava u nga matilweni.\n\n 4 - Hi rivalele swidjoho swa hina\n Tani hi leswi na hina rivalelaka lava hi djohelaka\n Bava u nga matilweni.\n\n 5 - U nga hi tsike hiwa mindzingweni\n  Kambe hi ponise Hosi\n  a hinkwaso le’swa ku biha\n Bava u nga matilweni.", false),

                Cancao("92","PaiNosso","Tatana werhu (82) (RONGA)","","Sol: Tatana\n  Tod: U nga matilweni (2x)\n Sol: A vito dzaku\n  Tod: A dzi twalisiwe (2 x).\n\n A wu te ku hine\n a nfumu waku (3x)\n  Ku rhandza kwaku\n  a ku yentxiwe\n a misaveni/swanga hi le tilweni\n\n Hi nyike namuntlha/swakudla swerhu (2x)\n swa siku dzinwana/ni dzinwana (2x)\n Hi dzivaleli/a swihono swerhu (3x)\n\n Swanga hi leswi/na hina hi\n  dzivalelaka/la'va hi djohelaka\n  U nga hi tsiki/hi wa miringweni\n Kambe hi huluxe/ku leswa ku biha.", false),

                Cancao("93","PaiNosso","Tatana weru u nga matilweni (83) (Ronga)","","Coro: Tatana weru u nga\n matilweni, Tatana (bis)\n  Yesu Kristo a hi dondzisile a ku,\n Ku vitana vito dzaku tatana.\n\n 1 - A vito dzaku a dzi twalisiwe (bis)\n  a wu te ku hine a nfumu waku.\n\n 2 - Ku rhandza kwaku a ku yentxiwe, a misaveni swanga\n hi le tilweni hi nyike nyamuntlha swakudla swerhu.\n\n 3 - Hi dzivaleli a swihono swerhu (bis)\n swanga hi leswi na hina hi va dzivalelaka la'va hi\n djohelaka\n\n 4 - U nga hi tsiki hi wa midringweni\n  kambe hi huluxe ku le swa ku biha.", false),

                Cancao("94","PaiNosso","Tsakani hosini aleluia","","Coro: Tsakani hosini aleluia (2x)\n\n 1 - Bava wa hina u nga Matilweni\n  A vito la Wena a li twalisiwe.\n\n 2 - A wu te ka hina a nfumu wa Wena\n Kurhandza ka Wena a ku yentxiwe.\n\n 3 - A misaveni tani he Tilweni\n  Hi nyike nyamuthla swakudla swa hina.\n\n 4 - Hi rivalele swidjoho swa hina\n Tani hi leswi na hina.\n\n 5 - Hi va rivalelaka la’va hi djohelaka\n Hi va rivalelaka la’va hi djohelaka.\n\n 6 - U nga hi tsike hi wa miringweni\n Kambe hi huluxe ka le’swa ku biha", false),

                Cancao("95","SaudacaoPaz","A hi ku xonga (84)","","A hi ku xonga, ku xonga ka swona\n  loko maxaka ma losana (bis).\n  Swi ta fanana, fanana na Yesu\n loko a nyikela ku rhula.\n Swa nyoxisa ngopfu-nogpfu\n  loko maxaka ma losana", false),

                Cancao("96","SaudacaoPaz","A ku twanana","","A ku twanana hi yoleyo ndlela ya vapfumeli (4X)\n A lirhandzu hi yoleyo ndlela ya vapfumeli ((4x)\n  A ku pfuxelana hi yoleyo ndlela ya vapfumeli (4x)\n\n Coro: A ku twanana, a ku twanana,\n  a ku twana i xilo xa hombe\n  A ku twanana, a ku twanana, \n  a ku twana xilhovo xa wutomi\n\n Ha kombeliwa hinkwerhu va makwerhu Hosini.\n  A ku landza minawu ya Hosi Tatana.\n  Hi maha munhu munwe hinkwerhu hi mbilu yinwe\n  Hi ta kulisa bandla lakwe hi ku twanana.", false),

                Cancao("97","SaudacaoPaz","Hi ku twanana (85)","","Coro: Hi ku twanana, hi ku twanana,\n Hi ku twanana va tirhela Hosi.\n\n 1 - A vafundisi, hi ku twanana,\n va swi kota ku hundzula vanhu.\n\n 2 - A vasacerdote, hi ku twanana\n  va swi kota ku txhumayela vanhu.\n\n 3 - A vatekani, hi ku twanana\n va swi kota ku yaka mindjangu.\n\n 4 - A makholwa, hi ku twanana,\n  ma swi kota ku khongela Hosi.\n\n 5 - A vanhu hinkwavo, hi ku twanana,\n  va swi kota ku hanya hi ku rhula.", false),

                Cancao("98","SaudacaoPaz","Rhandzanani (86)","","Rhandzanani nwe makriste		 Tlhevulanani (2x)\n  dzunisani a Xikwembu 		 hlekelelani (2x)\n na mi kombisa leswaku 		 dzunisani swinene\n  mi vana va Tatana munwe.  		Xikwembu Hosi Tatana.", false),

                Cancao("99","SaudacaoPaz","Yesu a vulile (87)","","1 - Yesu a vulile: Nawu wa mina\n  Hi ku rhandzana.\n\n Coro: Tshamani, tshamani ka mina\n   Hi ku rhandzana.\n\n 2 - Ndzi mi vekile nawu wa wumpshwa\n  I ku rhandzana.\n\n 3 - Ku fana ni le'swi ndzi mi rhandzaka\n  Rhandzanani.\n\n 4 - Loko mi rhandzana mi ta tikomba\n  Vana va Mina.\n\n 5 - Bava a hi rhandzile ku sunguleni\n   A hi mu rhandzeni.\n\n 6 - Lweyi a rhandzaka Nkulukumba\n   A rhandza ni makwavo.\n\n 7 - Varhandziwa, a hi rhandzaneni\n  Hi lirhandzu la Nkulukumba.", false),

                Cancao("100","CordeiroDeus","Wena xinyempfana","","1a Voz: Wena Xinyempfana xa \n Xikwembu Wena u Susaka\n\n Tod: Swidjoho swa vanhu\n\n 1a Voz: Swa vanhu-u\n\n Tod: Hi twele wusiwana (2x)\n\n 1a Voz:. Wena Xinyempfana -a\n\n Tod: Hi twele-é\n\n 1a Voz: Wena Xinyempfana\n\n Tod: Hi twele wusiwana (2x)\n\n 1a Voz: Wena Xinyempfana xa Xikwembu\n  Wena u Susaka\n\n Tod: Swidjoho swa vanhu\n\n 1a Voz:\n Swa vanhu-u\n\n Tod: Hi nyike ku rhula\n\n 1a Voz:. Wena Xinyempfana-a\n\n Tod: Hi lwele-é\n\n 1aVoz:Tod: Wena Xinyempfana\n Tod: Hi nyike ku rhula (2x)", false),

                Cancao("101","Comunhao","A mhunti loko yi twa torha (88)","","1 - A mhunti, loko yi twa torha,\n  Yi tsutsumela mati.\n Na mina ndzi twa torha, Hosi,\n  la Ngati yaku.\n\n Coro: Ndzi ta yamukela Miri waku;\n  Ndzi ta phuza kambe ni Ngati;\n Ndzi ta hanya na Wena, Hosi,\n Ka nfumu waku. (bis).\n\n 2 - Yesu, U murisi wa hina.\n Hi rise hi lirhandzu;\n  Yi yise livaleni lo nona\n  Ka mati yo tenga.\n\n 3 - Wena, U xithlangu xa hina,\n  Matimba ya vana vaku.\n Hi nyike timpswalo ta Wena.\n  Hi ta ku landza.\n\n 4 - Yesu, U wutomi la hina\n  Li taka he tilweni,\n Li hanyisa mimoya yerhu\n  Siku ni siku.\n\n 5 - Yesu a vitana hinkwavo:\n Tananini ka Mina,\n  Dlanani Miri lo'wa Mina\n Mi nwa ni Ngati.", false),

                Cancao("102","Comunhao"," Buyani hinkwenu (98)","","1 - Buyani hinkwenu, min'ndzi nhlovo\n  Laha ku humelelaka a wurombe;\n Buyani hinkwenu ku mbilu yanga,\n  Nhingenani, tshamisani ha hombe.\n\n Coro: Hosi yanga Yesu Kristo\n Mina ndzi ta ku landzela.(bis)\n\n 2 - A misava loko mi nga txeliwi\n  Mi nga pswala a mimintwa ntsena\n Hi xi swone ku mimoya yeru,\n  Loko mi nge na timpswalo le'tintene\n\n 3 - A swisiwana le'swi dyulaka\n  A tidyondzo ta le misaveni\n  Swi ta mi kuma minkanganyiso\n  Ni ku ka swi nga nhingeni tilweni.\n\n 4 - A xikolwe xinene xa tindyondzo\n  I mbilu ya Yesu Hosi yeru.\n  Hi dyondsise ha hombe Tatana\n  U huluxa a mimoya yeru.", false),

                Cancao("103","Comunhao","Hi nkama wa ku fa ka Yesu (89) (Ronga)","","Hi nkama wa ku fa ka Yesu a lalelile ni vadjondzisiwa,\n a teka Khati a khemelela a va yavela a ku:\n\n Tekani mi dia hinkwenu\n  Leyi i miri wanga\n Lowu u nyikeliwaka\n Hi kola kwhenu.\n\n Hi wugamu ndza ku lalela a tekile a ntseko, a khensa;\n loko a khensile a nyikela vadjondzisiwa a ku:\n\n Tekani mi nwa hinkwenu\n  Leyi i ngati yanga\n  Ya xipfumelelani\n  Le'xi nga heliki.", false),

                Cancao("104","Comunhao","Hi xuruse Hosi (Ronga)","","Hi xurhise Hosi\n  hi swakuda swa wutomi\n hi tiyise Hosi\n Andleneni ya lirhandru.\n\n 1 - Abukwini dra kuxwenga kutraliwile:\n  Ye'Mamana Maria akukombeli a hlolana,\n Dra kuyandrisa vhinyu a khubyini wa Khana.\n Hikola ka kudumba hikombela na hiku:\n\n 2 - Abukwmi dra kuxwenga kutraliwile:\n Leswaku wene Hosi u xurhisi a switrhungu\n Hi mahlolana lawa ya kuyandrisa a makati.\n  Hikola ka kudumba hi kombela na hi ku:\n\n 3 - Abukwini dra kuxwenga kutraliwile:\n  Leswaku a mirhi yaku i swakuda swa wutomi,\n  Leswaku a ngati yaku i swakunwa swa wutomi\n  Hikola ka kudumba hi kombela na hi ku.", false),

                Cancao("105","Comunhao","Hosi yesu hi karhele (90)",""," 1 - Hosi Yesu, hi karhele,\n  Hi helelwe hi matimba;\n  Hi tiyise, Hosi Yesu,\n Wena U xinkwa xa hina.\n\n 2 - U vulile: ndza tiyisa\n U swakudja swa wutomi\n  Swi xikeke he tilweni\n Leswi a hi tshembisiwe.\n\n 3 - Yesu, xinkwa xa tintsumi,\n Tana ka hina, U hi pfuna;\n  Loko Wena U na hina\n A hi hluliwi hi ntxhumu.\n\n 4 - Yesu, hi kombela ka Wena,\n U lomu xilalelweni,\n  Yentxa leswo hi ku rhandza\n  Hi masiku ni minkarhi", false),

                Cancao("106","Comunhao","Makweru vamukela Hosi (Ronga)",""," 1 - Lexi xipfumelelwano xanga\n ndri nga xi siya ka nwina\n  ndri nga mi nyiketa hinkwenu\n  lowu I Miri ni Ngati Yanga\n\n Coro: Makweru yamukela Hosi\n Yamukela Hosi\n  Makweru yamukela\n  Yamukela Hosi\n A Miri ni Ngati yakwe\n  Yamukela Hosi\n Buyani mu yamukela\n A Miri ni Ngati yakwe.\n\n 2 - Hosi Yesu i wutomi draku\n  Hosi Yesu i muhanyisi waku\n  Hosi Yesu i ndlela yaku\n Mu yamukeli hi lirandzo.\n\n 3 - Hosi Yesu a fele wene\n Hosi Yesu i Muhuluxi waku\n  Hosi Yesu i hinkwaswo swaku\n Mu yamukeli hi lirandzo.\n\n 4 - Hosi Yesu i ntamu waku\n  Hosi Yesu i wunene braku\n  Hosi Yesu i ku dumba kwaku\n  Mu yamukeli hi lirandzo.", false),

                Cancao("107","Comunhao","MIRI WA HOSI (92)","","1 - Solo: A Miri wa Hosi (3v.)\n  Coro: Ina, hi wo lo.\n  Todos: Wu ni ntamu Miri wa Hosi.\n\n 2 - Solo: A Ngati ya Yesu (3 v.)\n  Coro: Ina, hi yo le.\n  Todos: Yi ni ntamu a Ngati ya Yesu.\n\n 3 - Solo: A ku bindzeni (3 v.)\n  Coro: Ina hi yo lo.\n Todos: Wu ni ntamu Miri wa Hosi.\n\n 4 - Solo: A ku tsakeni (3 v.)\n Coro: Ina, hi yo le.\n Todos: Yi ni ntamu Ngati ya Hosi.", false),

                Cancao("108","Comunhao","Nambi ndzi li xisiwana (99) (Ronga)",""," 1 - Nambi ndzi li xisiwana, Hosi,\n  ndzi tshindzekela ka Wena\n U nga konti hi minandzu yanga;\n ndzi ni ndlala ndzi phameli.\n\n Coro:\n Oh..Oh..Oh..\n Oh Hosi yanga\n U nga konti minandzu yanga\n ndzi xurisi hi moya waku,\n ndzi ta tiya ndleleni yaku.\n\n 2 - Vaninga wutomi byanga, Hosi\n byi va lebyi phatimaka\n Hi laho ndzi ta ka tiya, Hosi\n ku hlaya ta Ritu draku.\n\n 3 - Ndzi nyiki ku xwenga kwaku, Hosi,\n loko ndzi da kati ledri\n Basisa vutomi byanga, Hosi\n Loko ndri nwa Nagati Yaku.\n\n 4 - Nambi loko a vanhu, we Hosi\n va phika ta nfumu waku,\n drivalela hikusa a va tivi\n Lexi va xi yentxaka.", false),

                Cancao("109","Comunhao","Ndza ku khinsamela",""," 1 - Ndza ku khisamela Xikwembu xanga\n Ndzi ku gandzela siku ni siku\n  A xilalelweni a ndzi ku voni......\n  Swaku wa ndzi randza se ndza swi vona....…\n\n CORO: Ndzi mu gandzelile eh, eh\n Ndzi mu gandzela (2X)\n  Ndzi mu gandzela siku ni siku\n\n 2 - Yesu malho yanga, a ma ku voni\n Ni mavoko yanga, a ma ku khumbi\n Kambe ndza pfumela, Rito la Wena\n Lowu i Miri wanga, Leyi i Ngati yanga\n\n 3 - A Xihambanwine a wu voneki\n Ni kola funtsi a wu voneki\n Kambe ndza pfumela we Hosi Yesu\n Leswaku U munhu ni swaku U Xikwembu", false),

                Cancao("110","Comunhao","Nghena Hosi mbilwini yanga (100) (Ronga)","","Coro: Nghena Hosi mbilwini yanga\n akuva u ndzi basisa (2x).\n\n 1 - Hosi Yesu hi wena ntsena musingati wanga\n Vutonwini hinkwadru\n ntamu wanga hi wene wa ku hlula sathana,\n nghena mbilwini yanga.\n\n 2 - Hosi Yesu hi wene ntsena mubrisi wanga\n Wutonwini hinkwadru\n hi wene u ni brisaka ka branyi bra lihlasi\n nghena mbilwini yanga.\n\n 3 - Hosi Yesu hi wene ntsena u mati yanga\n Wutonwini hinkwadru\n Loko ndzi nwa mati lawa ndzi ta huluxiwa,\n nghena mbilwini yanga.\n\n 4 - Hosi Yesu wene ntsena u Xinkwa xanga\n  Wutonwini hinkwadru\n Loko ndzi día Xinkwa lexi ndri ta huluxiwa,\n  nghena mbilwini yanga\n\n 5 - Hosi Yesu wena ntsena u ndlela yanga\n  Wutonwini hinkwadru\n loko u ndzi tsika wu swanga ndzi ta lahleka,\n nghena mbilwini yanga.", false),

                Cancao("111","Comunhao","Nwina mi nga ni ndlala (93)","","1 - Nwina mi nga ni ndlala,\n Tanani a mezeni; ( 2v.)\n Mi ta dla, mi xurha.\n\n 2 - Nambi mi nge na male,\n Tanani mi ta dla; (2 v.)\n Mi nga hakeli ntxhumu.\n\n 3 - Nwina mi nga ni torha.\n Tanani xihlobyeni (2 v.)\n Xa mati yo tenga.\n\n 4 - Nambi mi nge na male,\n Tanani mi ta nwa, (2 v.)\n Mi nga hakeli ntxhumu\n\n 5 - Loko mi ndzi yingisa,\n Mimoya ya nwina (2 v.)\n Yi ta kuma ku hanya.", false),

                Cancao("112","Comunhao","Pfumela wa vitaniwa (94)","","Coro: Pfumela wa vitaniwa (3 v.)\n  Le muntini wa Xikwembu.\n\n 1 - Tana wena u nga ni ndlala (2 v.)\n  U ta dla muzimba wa Yesu\n  Le muntini wa Xikwembu.\n\n 2 - Tana wena u nga ni torha (2 v.)\n U ta phuza Ngati ya Yesu\n Le muntini wa Xikwembu.\n\n 3 - Tana wena u karhaleke (2 v.)\n  Tana wena u nga xisiwana\n  Le muntini wa Xikwembu.\n\n 4 - Tshunelani nwina hinkwenu (2 v.)\n  Yena Yesu a mi vitana\n Le muntini wa Xikwembu.\n\n 5 - Yingisani, nwina makholwa, (2 v.)\n A marito ya Hosi Yesu\n  Le muntini wa Xikwembu.\n\n 6 - Rito lakwe la hanyisa (2 v.)\n  Rito le'li nyika wutomi\n Le muntini wa Xikwembu.", false),

                Cancao("113","Comunhao","Tanani mi ta lalela (96)","","1 - Tanani, nwina makholwa,\n  Nwina mi ndzi rhandzaka;\n  Tanani hinkwenu,\n  Tanani mi ta lalela.\n\n 2 - Tanani, nwina vayendzi,\n Nwina mi karhaleke\n Hi ndlala ni torha,\n  Tanani mi ta lalela.\n\n 3 - Tanani, nwina vabava,\n Na nwina vamamani,\n Tanani, tanani\n Tanani mi ta lalela.\n\n 4 - Tanani, nwe vatsongwani\n\n Ndza mi rhandza swinene;\n  Tanani hinkwenu,\n  Tanani mi ta lalela.\n\n 5 - Tanani, nwe swisiwana,\n  Na nwina mi hluphekaka\n  Masiku hinkwawo,\n  Tanani mi ta lalela.\n\n 6 - Tanarii, nwe vafeliwa,\n  Nwina mi rilaka,\n  Masiku hinkwawo,\n  Tanani mi ta lalela.\n\n 7 - Tanani, nwina vadjohi,\n  Nwina mi pfumalaka\n  Ku rhula mbilwini;\n  Tanani mi ta lalela.", false),

                Cancao("114","Comunhao"," Trhindrekelani (101) (Ronga)","","1 - Trhindrekelani hinkwenu a mezeni wa Hosi!\n Mi da kati ledro i nga miri yakwe\n  ma rhambiwa hinkwenu a mezeni wa Hosi\n mi ta nwa a vinyo ledro i nga ngati yakwe.\n\n Coro: Ha vitana hinkwenu mungani ndlala\n ali: trhindrekelani a mezeni wa wutomi!\n  ku Yene akuna ndlala nambi tora\n ku Yene a ku na wusiwana!\n\n 2 - Mi ta yentxa lewswo hi masiju hinwawu\n loko mi drimuka lwey' a mi feliki\n  Lwey' a mi bratrheliki swihono hinkwaswu\n  Lw' a mi navelelaka a nfumo wakwe.", false),

                Cancao("115","Comunhao","Yimba lirimi la mina (97)","","1 - Yimba, lirimi la mina,\n  Dzunisa a Xilalelo;\n Twalisa Muhuluxi\n Lomu ka Xikwetsimo.\n\n 2 - Hi vona swongi xinkwa,\n  Kasi i Miri wa Yena;\n  Hi vona swongi i vinyo,\n Kasi i Ngati ya Yena.\n\n 3 - A vitana hinkwavo,\n  Tanani, tananini;\n Mi ta dja Miri wa Mina.\n  Mi ta nwa ni Ngati.\n\n 4 - Hi luva, hi twalisa\n Mubasisi wa hina;\n  Hi minkama hinkwayo\n Hi tiva Hosi ya hina.\n\n 5 - La'va nga basa mbilu\n  Va nga mu yamukela;\n Yesu Kristo a nga ngheni\n Ka timbilu to biha.", false),

                Cancao("116","Gracas","Banani, banani nkulungwana","","Coro: Banani, banani nkulungwana (2X)\n Hi tsakela Hosi, a hi nyoxeni a hi tsakeni timbilwini (2X)\n\n 2 - Hi kumile a makateko ka siku la nyamuntlha (2X)\n\n 3 - Hi kumile a ku tsaka ka siki la nyamuntlha (2X)\n\n 4 - Hi kumile a huluxo ka siku la nyamuntlha\n\n 5 - Hi kumile a ntamu ka siku la nyamuntlha", false),

                Cancao("117","Gracas","Bongani nkulukumba (107)","","Coro: Bongani, bongani,\n bongani Nkulukumba.\n\n 1 - Bongani Nkulukumba,\n nwe mintirho ya yena.\n\n 2 - Bongani Nkulukumba\n  Vanhu ni tiko la hina\n\n 3 - Bongani Nkulukumba,\n  tinyeleti ta tilo.\n\n 4 - Bongani Nkulukumba,\n Nweti ni dlambu la tilo.\n\n 5 - Bongani Nkulukumba,\n wusiku ni nhikanhi.\n\n 6 - Bongani Nkulukumba,\n swiharhi swa nhova\n\n 7 - Bongani Nkulukumba,\n  nwe vanhu hinkwenu.\n\n 8 - Bongani Nkulukumba\n  Bava ni Nwana\n ni Moya Wo kwetsima", false),

                Cancao("118","Gracas","Hina ha ku dzunisa","","1 - Hina ha ku dzunisa Hosi\n  Xikwembu nwinyi wa ntiyiso,\n moya wanga dzunisa Hosi\n  Wena u Tatana u hanyaka\n minkama hinkwayo, moya wanga\n dzunisa Hosi\n\n coro: Dzunisa, moya wanga (3x)\n Hosi ndzi ta ku dzunisa\n\n 2 - Tintsumi tatilo ta ku twalisa Hosi\n  Moya wanga dzunisa Hosi\n Hi wena Hosi ya ku kwetsima,\n Hosi va makandza ya tilo\n moya wanga dzunisa Hosi\n\n 3 - A wukulu la wena Hosi\n li tata misava hinkwayo moya wanga\n dzunisa Hosi a tinsasi a\n  matilweni ta ku twakisa\n Moya wanga dzunisa Hosi.\n\n 4 - A kereke ya wena Hosi ya ku twalisa,\n  moya wanga dzunisa Hosi\n Nwana ni moya wa ku kwetsima\n ha ku twalisa,moya wanga.\n\n 5 - Hosi Yesu u Hosi ya matilo\n  moya wanga dzunisa Hosi\n U xikile hi le matilweni a kuva u ta\n hi huluxa moya wanga.", false),

                Cancao("119","Gracas","Hina ha ku khensa (Ronga)","","Coro: Hina ha ku khensa.Tods:\nAleluia\n Hina ha ku khensa, we Hosi, ah, ah\n Hina ha ku khensa, we Hosi (bis)\n\n 1 - Ha ku khensa, we Hosi hi siku le la nyamuntlha ah,ah.\n Hina ha ku khensa we Hosi (bis).\n\n 2 - Ha ku khensa, we Hosi, hi wutomi leli U hi nyikike ah,ah.\n Hina ha ku khensa we Hosi (bis).\n\n 3 - Ha ku khensa, we Hosi hi vana lava U hinyikike ah, ah.\n  Hina ha ku khensa we Hosi (bis).\n\n 4 - Ha ku khensa, we Hosi hi timpswalo leti U hi nyikike ah, ah.\n  Ha ku khensa we Hosi (bis).\n\n 5 - Ha ku khensa, we Hosi, hi lirandzu leli U hi nyikike ah,ah.\n Ha ku khensa we Hosi (bis).", false),

                Cancao("120","Gracas","Hosi yanga (109)","","1 - Hosi yanga, ndzi vona ku saseka\n Ka hinkwaswo leswi U swi yentxeke:\n Tinyeleti ku dzuma ka matilo\n Ni matilo ma kombaka Wena.\n\n Coro: We moya wanga, khensa Xikwembu.\n We moya wanga, khensa Xikwembu.\n  U lwe'nkulu, Wena Hosi!\n\n 2 - Le xihlahleni loko ndzi tsemakanya\n  Ndzi yingisa a swinyanyana swi yimba\n  Ndzi languta ko'le hansi ka ntsava\n  Na ku tsutsuma a xinambyana\n\n 3 - Swo kombisa leswaku xo Xikwembu\n Xi hi nyikile a Nwana wa Xona.\n Kasi nsinyeni ndzhwalo wanga wa suka\n Ndzi taleliwa hi ku lulama.\n\n 4 - Loko Yesu a kota a vuya laha,\n A ndzi teka, ndzi ta tsaka swinene\n Hi ku khensa ka ku titsongohata\n  Mahlweni ka ye, n'ta ku: U lwe'nkulu!", false),

                Cancao("121","Gracas","Khanimambo Hosi yanga (111)",""," Coro: Khanimambo, Hosi yanga,\n Khanimambo, Tatana,\n  Khanimambo, Murisi wanga,\n Khanimambo, Xikwembu xanga.\n\n 1 - Ha ku bonga, wena Tatana\n  Hi swivumbiwa swaku hinkwaswo\n Ni mintirho ya wena hinkwayo\n Ni hinkwerhu vana va wena.\n\n 2 - Ha ku bonga, wena Tatana,\n Hi makwerhu wa hina dlambu\n Li vaningelaka misava.\n Li kulisa swimila swa nhova.\n\n 3 - Ha ku bonga, wena Tatana,\n Hi makwerhu wa hina nwheti\n Ni vamakwerhu tinyeleti\n  Le'ti vaningaka wusiku.\n\n 4 - Ha ku bonga, wena Tatana,\n Hi makwerhu wa hina moya\n Lo'wu hlakahlaka minsinya\n  Ni marhefu la'ma nga mpfukeni.\n\n 5 - Ha ku bonga, wena Tatana,\n Hi makwerhu wa hina misava\n Le'yi hi nyikaka mihandzu\n  Ni swiluva, byanyi hinkwalo.\n\n 6 - Ha ku bonga wena Tatana,\n  Hi makwerhu wa hina mati\n  Lawa ma hi timaka torha\n Ni ku pfunisa masimo\n\n 7 - Ha ku bonga wena Tatana\n Hi makwerhu wa hina ndzilo\n Wu hi vaningaka wusikwini\n  Ni ku kufumeta swisiwana.\n\n 8 - Ha ku bonga wena Tatana,\n  Hi makwerhu wa hina lifo\n  Le'li tekaka vanhu hinkwavo,\n  Li va yisa mahleni ka Wena\n\n 9 - Ha ku bonga, wena Tatana,\n Hi vamakwerhu la’vo rhula\n  Va swi kotaka ku rivalela\n Va hlupheka hi vito la Wena.", false),

                Cancao("122","Gracas","Ku vonakala ka tilo (121) (Ronga)","","1 - Ku vonakala ka tilo\n Ku le timbilwini teru,\n Hi ku pfumela ku Yesu\n  Hi tshama ku yene\n A va a hi nyika ntamu\n  Hi bapatiso dzakwe\n  Hi nga ka hi nga thleleli ndzhaku;\n  Hi ta yimba aleluya,\n  Hi twalisa Hosi\n Na ku khapa a ku nyonxa\n  A timbilwini teru;\n  Leswi a hi yingeliki\n  Na hi dzila a munyameni;\n  Hi nga ka hi  nga theleli ndzhaku\n\n Coro:\n O we Yesu, Hosi yeru, ha ku bonga Muhuluxi.\n  Hi nga ka hi nga theleli ndzhaku.\n Hi ta lamba ndleleni ya livoningo dzaku.\n Hi nga ka hi nga theleli ndzhaku.\n\n 2 - Swoswi, we Hosi, yingela\n  A ku kongota kweru:\n Hi khongelela a tiko\n Dzaku dzi suvaka;\n  Dzi nyika a livoningo\n Ni xihlayela-mfuri,\n  Dzi kholwa, dzi nga thleleli ndzhaku.\n Hi kombelela vabyisi\n Ni valeri va Rito\n  Akuva ntiro wavu\n  Wu wa lo'wa ku xwenga.\n  U huluxa laVanyigi\n  Va nga le munyameni,\n  Va kholwa, va nga thleleli ndzhaku.", false),

                Cancao("123","Gracas","Loko u kumi a makateko U KUMI A MAKATEKO (122) (Ronga)","","1 - Loko u kumi a makateko\n  yimbelela Nkulukumba (2x)\n\n 2 - Loko u kumi a timpswalu\n yimbelela Nkulukumba.\n\n Coro: Alelluia, Aleluia\n aleluia lwe a nga henhla, henhla\n lwe a nga kone makaze kwerhu\n a hi nyikaka a wukosi dzaku. - 3. Loko u kumi a huluxu\n yimbelela Nkulukumba.\n\n 4 - Loko u kumi lirhandzu\n Yimbelela Nkulukumba\n\n 5 - Loko u kumi a ku hanya\n  yimbelela Nkulukumba.", false),

                Cancao("124","Gracas","Mina ndza ku bonga (Ronga)","","Coro: Mina ndza ku bonga\n Hosi ni Xikwembu xanga\n Leswi u ndzi yentseke\n  Mutirheli waku. (2x)\n\n 1 - Hosi ya wunene\n  Mina ndza ku bonga\n  Leswi undzi yentseke\n  Mutirheli waku.\n\n 2 - Masiku yo tala\n A manabyalanga\n Kambe undzi tiyisile\n A ndleleni yaku.\n\n 3 - Hosi Xikwembu xanga\n hi umbilu ni liranadzu\n undzi fembisile\n hi ndlela yo lulama.\n\n 4 - Inkomu Hosi\n Xilhovo xa utomi\n Leswi undzi kombiseke\n A xihundla swa tilu.\n\n 5 - Inkomu inkomu\n We Xikwembu xanga\n  Mbilu ni hika la mina\n Swa tlula hi ku tsaka.\n\n 6 - Banane Nkulungwane\n  Ka siku leli\n  Hi tlangela Xikwembu\n  Hi nyiku yavu Fundrisi.\n\n 7 - Ndza ku kombela Hosi\n  A makateku yaku\n Ndzi ta tiyisela\n A ndleleni yaku", false),

                Cancao("125","Gracas","Mina we Hosi (Ronga)","","1 - Mbilu yanga ya kina, kina,\n yi khensa Xikwembu xanga.\n Coro: mina we hosi,\n Ndzi ta ku landzela (3x)\n Ndzi lhaya ndzi ku ina.\n\n 2 - Minenge yanga, ya famba, famba,\n yi khensa Xikwembu xanga.\n\n 3 - Mavoko yanga ma khoma khoma,\n  ma khensa Xikwembu xanga.\n\n 4 - Mahlu yanga ma vona vona,\n ma khesa Xikwembu xanga.", false),

                Cancao("126","Gracas","Na ku bonga (113)","","Solista: Na ku bonga\n Todos: Mina na ku bonga\n Solista: Na ku bonga\n Todos: Mina na ku bonga Hosi (4x)\n\n 1 - Ni bonga Hosi Bava mina...\n  muyentxi wa tilo ni misava...\n\n 2 - Ni bonga Hosi Bava, mina ...\n  muyentxi wa vanhu hinkwavo, mina ...\n\n 3 - Ni bonga Hosi Bava, mina ...\n  nwinyi wa ntamu hinkwawo, mina ...\n\n 4 - Ni bonga Hosi Bava, mina ...\n  Hosi ya swilo hinkwaswo, mina ...", false),

                Cancao("127","Gracas","Na ndza he kule na Yesu (112)","","1 - Na ndza he kule na Yesu\n A ndzi fa hi xirhami.\n  Kambe ku rila ka mina a ku twile.\n Se a ndzi funengetile, Nguvu a ndzi vuyisa.\n  Swoswi se ndzi le ku tshamisekeni.\n\n Coro:\n Nkomu ka Yesu! Twakala Aleluia!\n  Ndzi pfuneni ku dzunisa Muhanyisi\n Hik'va ndzi pfule tinyangwa\n  Ta moya ka swa tilo.\n  Swoswi se ndzi le ku tshamisekeni.\n\n 2 - Hambi tiko li ndzi komba\n  Swilo swa lona swimpshwa,\n Kambe a ndzi swi naveli swoleswo.\n  Moya wa mina wu tilweni\n La ku humaka dlambu.\n Swoswi se ndzi le ku tshamisekeni.\n\n 3 - Ni wunyingi la nsuku\n  Li nge ndzi susi laha,\n  Hambi muringi a ndzi hlupha ngopfu,\n Leswi ndzi hlayisekeke\n Ka Xikwembu xa mina\n  Swoswi se ndzi le ku tshamisekeni.\n\n 4 - Na hi fikile koseno Ka tindlela to basa,\n  Na hi wele khwatsi nambu wa Yordan\n  Ta ndzi kuma ndzi tlangela\n  Nyangweni wa yindlu ya mina.\n  Swoswi se ndzi le ku tshamisekeni.", false),

                Cancao("128","Gracas","Ndra ku khensa (Ronga)","","Refrão: Ndra ku khensa Nkulukumba wanga\n  Leswi u nga ndri vitana\n A makari ka swivumbiwa\n Ndri tava mutireli waku.\n\n 1 - Mine Hosi a ndri famba ka munyama lo'wukulu\n Ndri nga yi voni ndlela\n Kambe Wene hi wumbilu byaku, ut e:\n  Buya ndri ku komba ndlela.\n\n 2 - Mine Hosi a ndro trhama ni kondla mavoko\n  Ndri lavisela vambeni\n Kambe Wene hi wumbilu byaku, u te:\n Buya a ntirhu hi lowu.\n\n 3 - Mine Hosi a ndri hanya ni nga tivi ku fakaza\n  Niku a ndri tivi txumu\n  Kambe Wene hi wumbilu byaku, u te:\n  Buya ndri ku komba a rhitu.\n\n 4 - Mine Hosi a ndro hanya na ndri\n  nga khongoti\n  Ku khongota a ndri swikoti Kambe Wena hi wumbilu byaku,\n ute:\n Buya hi bula xikanwe.\n\n 5 - Mine Hosi a ndri nga swi koti a kukhensa\n  Ndriku, ndri ta khensa yini\n Kambe Wene hi wumbilu byaku,u te:\n  Buya a timpswalu hi leti.", false),

                Cancao("129","Gracas","Ndza khensa hikuva Yesu (114)","","1 - Ndza khensa hikuva Yesus a kona\n  Wa vitana mina ku ta ka yena.\n Wa ndzi hanyisa loko ndzi kholwa.\n  Hi swona, ndza ku khongela Wena.\n\n Coro: Ndza ku pfumela, wena Hosi.\n  Ndzi katekise mina wa wena.\n Hosi ya mina yi ta ndzi pfuna.\n Nyamunthla ndza tshemba yena.\n\n 2 - Mu yingiseni Muhanyisi Lweyi.\n  A tele ku hanyisa vanhu.\n  A ni ntamu wa ku hi basisa\n  Loko hi pfumela ku mu kombela.\n\n 3 - Tatana wa hina wa hi vitana,\n  A ku: Tanani mi nga lahleki.\n  A hi fambeni hi ya ka yena.\n Hakunene hi ta khensa Yesu.", false),

                Cancao("130","Gracas","Ndza ku bonga","","SOL: Ndza ku bonga we Hosi, hossana\n TOD: Hossana le henlha\n SOL: Ka siku leli la nyamuntlha hossana\n TOD: Hossana le henlha\n\n CORO: SOL: Hossana\n TOD: Hossana\n SOL: Hossana\n TOD: A hi bongeni\n SOL:Hossana\n TOD: Hossana le henlha.\n\n 2 - Tinsumi ta yimbelele. Hi siku leli la nyamuntlha.\n\n 3 - A makateko ya Wena. Ma hi nyika matimba.\n\n 4 - Siku ni siku we Hosi. Hina ha ku khisamela.\n\n 5 - Hosi Tatana wa lirhandzu. Hi nyike lirhandzu laku.\n\n 6 - Hi wena wa ku kwetsima. Nkulukumba wa matilo.\n\n 7 - Hi huluxe we Hosi. Hina vana va Wena.\n\n 8 - Banani minkulungwana. Hi nyiko leyi ya nyamuntlha.", false),

                Cancao("131","Gracas","Ndza ku bonga, hosi Yesu (124)","","1 - Ndza ku bonga, Hosi Yesu;\n Ndza ku bonga, We Hosi yesu\n Aleluia!\n Ndza ku Bonga ...\n\n 2 - U ni ntamu, Hosi Yesu;\n U ni ntamu, We Hosi Yesu u munene,\n Aleluia!\n U ni ntamu ...\n\n 3 - U xwengile, Hosi Yesu;\n U xwengile, We Hosi Yesu\n Aleluia!\n U xwengile...\n\n 4 - U munene, Hosi Yesu\n We Hosi Yesu;\n Aleluia!\n U munene...\n\n 5 - Wa ndzri randza, Hosi Yesu;\n Wa ndzi randza, We Hosi \n Yesu Aleluia\n Wa ndzi randza...", false),

                Cancao("132","Gracas","Ndza ku mbonga nkulukumba (123) (Ronga)","","Coro: Ndza ku bonga nkulukumba\n Leswi wene u ndzi hlawuliki\n A makarhi ka swiwumbiwa\n Akuva ndzi ku tirhela.\n\n 1 - Hosi wumbilu dzaku, le ku mine i dzi kulu ngopfu,\n leswi u ndzi kombisiki, ta xihundla xa tilo.\n\n 2 - Malembe la matsongo, ndzi ku tirhelile hi lirhandzu\n na wumbilu dzaku Hosi ndzi nyikile ntamu.\n\n 3 - Swoswi we Hosi yanga, yingela a xikhongoto xanga,\n xitkhongoto xa ku khensa, timpswalo ni lirhandzu.\n\n 4 - Se Hosi yanga swoswi, ndzi khensa ngopfu a\n matimba yaku, ma ndzi pfuna a ku hlula, a valala va moya.", false),

                Cancao("133","Gracas","Ndzi ta bonga ha yini","","1 - Ndzi ta bonga hayini ka wena Tatana wanga\n Xikwembu xanga aa a a\n Xikwembu xanga, lweyi a nga ndzi yentxa munhu\n Xikwembu xanga aa aa.\n\n Coro: Ka wena u nga ndzi vitana\n Ka wena u nga ndzi huluxa. (2x)\n Solista: Ndzi ta bonga hayini\n Ka wena Xikwembu xanga\n\n 2 - Ndzi ta bonga hayini ka wena Hosi Yesu.\n Xikwembu xanga aa, aa\n Xikwembu xanga, lweyi a nga ndzi huluxa\n Xikwembu xanga aa, aa\n\n 3 - Ndzi ta bonga hayini ka wena Moya ku\n Xwenga Xikwembu xanga, aa, aa\n Xikwembu xanga, lweyi a nga ndzi pangalata\n Xikwembu xanga aa, aa", false),

                Cancao("134","Gracas","Ndzi ta tlangela Yehova (115)","","1 - Ndzi ta tlangela Hosi ha yini? (2.v.)\n Minkateko hinkwayo ya Yona Mi le henhla ka mina\n\n Coro: Ku tsakeni ka mina (2 v.)\n N'ta mu tlangela ha yini?\n Minkateko hinkwayo ya Yona\n Mi le henhla ka mina\n\n 2 - A ndzi tumbuluxile Hosi\n A ndzi mahile Nwana wa Yona.\n Minkateko hinkwayo ya Yona.\n Mi le henhla ka mina\n\n 3 - A ndzi huluxile ka swiwonho\n Hi Nwana wa Yena Yesu Kristo.\n  Minkateko hinkwayo ya Yena\n  Mi le henhla ka mina\n\n 4 - Wa ndzi basisa, wa ndzi tiyisa\n  Hi ntamu wa Moya wa ku xwenga.\n  Minkateko hinkwayo ya Yena\n  Mi le henhla ka mina.", false),

                Cancao("135","Gracas","Nkulumba ndza ku bonga swinene (116)","","Coro: Nkulukumba ndza ku bonga\n swinene (4x)\n Nkulukumb\n\n 1 - Hosi yanga,\n Hosi ya matilo,\n  muvumbi wa hina,\n  nwinyi wa hinkwaswo\n\n 2 - Hosi ya matimba\n hi nyike a ntamu\n hi ta ku tirhela,\n  Siku ni siku\n\n 3 - Hosi yanga,\n Hosi ya matimba,\n hi nyike lirhandzu\n siku la nyamunthla.\n\n 4 - A beneni\n  a nkulungwana\n siku la nyamuntlha\n Hi kombisa ku tsaka.", false),

